import React, { useEffect, useState } from 'react';

const mockNewsData = [
  { title: "Major Championship Changes Shake Up the Wrestling World", time: "Just now", views: "3.5k" },
  { title: "Championship Match Results: New Champion Crowned", time: "2 hours ago", views: "1.2k" },
  { title: "Behind the Scenes: Training Regimen Revealed", time: "4 hours ago", views: "900" },
  { title: "Upcoming Events: What to Expect This Season", time: "6 hours ago", views: "2.1k" },
];

const News = () => {
  const [newsData, setNewsData] = useState([]);

  useEffect(() => {
    // simulate fetch
    setTimeout(() => setNewsData(mockNewsData), 500);
  }, []);

  return (
    <>
      <style>{`
        :root {
          --dark-bg: #000;
          --secondary-bg: #1a1a1a;
          --border: #333;
          --main-purple: #522D92;
          --text-light: #fff;
          --text-muted: #666;
        }
        .news-page {
          background: var(--dark-bg);
          color: var(--text-light);
          min-height: 100vh;
          display: flex;
          flex-direction: column;
        }
        .latest-news {
          flex: 1;
          padding: 2rem;
          max-width: 1200px;
          margin: auto;
        }
        .news-grid {
          display: grid;
          grid-gap: 1.5rem;
        }
        @media (min-width: 768px) {
          .news-grid { grid-template-columns: repeat(2,1fr); }
        }
        @media (min-width: 1024px) {
          .news-grid { grid-template-columns: repeat(3,1fr); }
        }
        .news-card {
          background: var(--secondary-bg);
          border: 1px solid var(--border);
          border-radius: 8px;
          padding: 1.5rem;
        }
        .news-card h3 {
          margin: 0 0 .75rem;
          font-size: 1.1rem;
        }
        .news-card .meta {
          font-size: .8rem;
          color: var(--text-muted);
        }
        footer.app-footer {
          background: var(--secondary-bg);
          padding: 2rem;
          text-align: center;
        }
        .social-links {
          margin-top: 1rem;
          display: flex;
          justify-content: center;
          gap: 1rem;
        }
        .social-links a {
          display: block;
          width: 32px;
          height: 32px;
        }
        .social-links img {
          width: 100%;
          height: auto;
        }
      `}</style>

      <div className="news-page">
        <section className="latest-news">
          <h2>Latest News</h2>
          <div className="news-grid">
            {newsData.length === 0
              ? <p>Loading…</p>
              : newsData.map((item,i) => (
                <div key={i} className="news-card">
                  <h3>{item.title}</h3>
                  <div className="meta">
                    <span>{item.time}</span> • <span>{item.views} views</span>
                  </div>
                </div>
              ))
            }
          </div>
        </section>

        <footer className="app-footer">
          <p>&copy; 2025 Burn It Down. All rights reserved.</p>
          <div className="social-links">
            <a href="https://facebook.com/…"><img src="facebook.png" alt="Facebook"/></a>
            <a href="https://youtube.com/…"><img src="youtube.png" alt="YouTube"/></a>
            <a href="https://instagram.com/…"><img src="instagram.png" alt="Instagram"/></a>
          </div>
        </footer>
      </div>
    </>
  );
};

export default News;
