import { useState, useRef } from 'react';

const Product = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);
    // Refs for scrolling to sections
    const homeRef = useRef(null);
    const shopRef = useRef(null);
    const latestNewsRef = useRef(null);

    const scrollToSection = (ref) => {
        ref.current?.scrollIntoView({ behavior: 'smooth' });
        setIsMobileMenuOpen(false);
    };
    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = 'Media-kit.pdf';
        link.download = 'MediaKit.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setIsMobileMenuOpen(false);
    };

    // Safe icon components using Unicode/HTML entities
    const MenuIcon = () => <span style={{fontSize: '20px'}}>☰</span>;
    const CloseIcon = () => <span style={{fontSize: '20px'}}>✕</span>;
    const UserIcon = () => <span style={{fontSize: '18px'}}><img src="Vector.png"/></span>;
    const ShoppingBagIcon = () => <span style={{fontSize: '18px'}}><img src="shoppingbag.png"/></span>;

    const NavLinks = ({ isMobile = false, onItemClick }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
            <button onClick={() => onItemClick ? onItemClick(homeRef) : scrollToSection(homeRef)} className="nav-link">
                Home
            </button>
            <button onClick={() => onItemClick ? onItemClick(shopRef) : scrollToSection(shopRef)} className="nav-link">
                <img src="arrow-right.png" className="nav-arrow"/><span>Shop</span>
            </button>
            <button onClick={() => onItemClick ? onItemClick(latestNewsRef) : scrollToSection(latestNewsRef)} className="nav-link">
                →Latest News
            </button>
            <button onClick={handleMediaKitDownload} className="nav-link">
                Media Kit
            </button>
            <button onClick={() => console.log('Contact Us clicked')} className="nav-link">
                Contact Us
            </button>
        </nav>
    );

    return (
        <>
            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                body {
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    background-color: #000;
                    color: white;
                    line-height: 1.6;
                }

                .container {
                    min-height: 100vh;
                    background-color: #000;
                }

                /* Discount Banner */
                .discount-banner {
                    background-color: #D3D3D3;
                    color: black;
                    text-align: center;
                    padding: 0.5rem;
                    font-size: 0.85rem;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 0.3rem;
                    position: relative;
                }

                .discount-banner .discount-icon {
                    display:flex;
                    align-items:center;

            }
                .discount-close {
                    position: absolute;
                    right: 1rem;
                    background: none;
                    border: none;
                    color: white;
                    cursor: pointer;
                    font-size: 18px;
                    padding: 0.2rem;
                    display: flex;
                    align-items: center;
                    justify-content: center;
}

.discount-close:hover {
    color: #ccc;
}

                /* Header Styles */
                .header {
                    background-color: white;
                    border-bottom: 1px solid #e0e0e0;
                    padding: 0.8rem 0;
                    position: sticky;
                    top: 0;
                    z-index: 1000;
                }

                .header-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    background-color: white;
                    position: relative;
                }


                .logo {
                    font-size: 1.4rem;
                    font-weight: bold;
                    color: #000;
                    text-decoration: none;
                    background-color: white;
                    flex: 1;
                    margin-left: 0.5rem;

                }

                .desktop-nav {
                    display: none;
                    gap: 2.5rem;
                    background-color: white;
                    position: absolute;
                    left: 50%;
                    transform: translateX(-50%);
                      z-index: 10;
                }

                .nav-link {
                    background: none;
                    border: none;
                    color: Black;
                    background-color: white;
                    text-decoration: none;
                    font-size: 0.9rem;
                    cursor: pointer;
                    padding: 0.5rem 0;
                    font-weight: 500;
                    transition: color 0.3s;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    white-space: nowrap;
                }
                .nav-link img {
                   display: inline-block;
                   width: 1rem;
                   height: auto;
                   margin: 0;
                }
                .nav-arrow {
                   width: 1rem;
                   height:auto;
                   margin-right: 0.25rem;!important
                   display: inline-block;

                }


                .nav-link:hover {
                    color: #000;
                    background-color: white;
                }

                .nav-link1.no-underline {
                   text-decoration: none;

                }

                .header-right {
                    display: flex;
                    align-items: center;
                    gap: 0.2rem;
                    background-color: white;
                    flex: 1; /* Take up available space */
                    justify-content: flex-end;
                }

                .header-icon {
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                    border-radius: 4px;
                    transition: background-color 0.3s;
                }

                .header-icon:hover {
                    background-color: #f5f5f5;
                }

                .mobile-menu-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 10;
                    background-color: white;
                    z-index: 2000;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                }

                .mobile-menu-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 2rem;
                }

                .mobile-menu-header .logo {
                    color: white;
                }

                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem;
                    background-color: white;
                    color: white;
                }

                .mobile-nav .nav-link {
                    font-size: 1.1rem;
                    padding: 1rem 0;
                    border-bottom: white;
                    text-align: center;
                    color: black;
                }

                .close-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Hero Section */
                .hero {
                    background: #000;
                    padding: 2rem 0;
                    text-align: center;
                    position: relative;
                }

                .hero-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .hero-logo {
                    width: 100%;
                    max-width: 800px;
                    height: 400px;
                    margin: 0 auto;
                    background-color: black;
                    border-radius: 8px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #666;
                    font-size: 1rem;
                }
                    .hero-logo img {
                    width: 100%;
                    height: 76%;
                    display:block;
                    object-fit: cover;
                    object-position: center;
                }

                .tickets-btn {
                    position: absolute;
                    top: 2rem;
                    right: 8.8rem;
                    background-color: #fff;
                    color: #000;
                    border: none;
                    padding: 0.7rem 1.2rem;
                    border-radius: 4px;
                    font-weight: 600;
                    cursor: pointer;
                    transition: transform 1s;
                    font-size: 0.9rem;
                }

                .tickets-btn:hover {
                    transform: translateY(-1px);
                }

                /* Latest News Section */
                .latest-news {
                    padding: 3rem 0;
                    background-color: white;
                }

                .section-container {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .section-title {
                    font-size: 1.8rem;
                    margin-bottom: 2rem;
                    color: #000;
                    font-weight: 600;
                    gap: 0.5rem;
                }
                .section-arrow {
    font-size: 1.2rem;
    color: #666;
    cursor: pointer;
    transition: color 0.3s;
}

                .news-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
                    gap: 1.5rem;
                    grid-auto-flow: row;
                }

                .news-card {
                    background-color: transparent;
                    border-radius: 8px;
                    overflow: hidden;
                    box-shadow: none;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                    position: relative;
                }

                .news-card:hover {
                    transform: translateY(-4px);
                    box-shadow: 0 4px 16px rgba(255, 255,255,1);
                }
                .news-image {
                    width: 100%;
                    height: 420px;
                    object-fit: cover;
                    background-color: transparent;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #999;
                    font-size: 0.85rem;
                    border-bottom: none;
                }

                .news-image img {
                   width: 100%;
                   heigh:100%
                   object-fit:cover
                }

                .news-content {
                    padding: 1.2rem;
                }

                .news-label {
                    /* Removed position: absolute; top; left; */
                background-color: white;
                color: black;
                padding: 0.25rem 0.6rem;
                border-radius: 12px;
                font-size: 0.75rem;
                font-weight: 600;
                display: inline-block; /* Changed to inline-block */
                margin-bottom: 0.7rem; /* Added margin-bottom for spacing */
                text-transform: uppercase;
                position: absolute; /* Added back for specific placement within the card as seen in HOMEPAGE.jpg */
                top: 12px; /* Adjusted as per HOMEPAGE.jpg */
                left: 12px; /* Adjusted as per HOMEPAGE.jpg */
                z-index: 10;
                }

                .news-title {
                    font-size: 1rem;
                    margin: 0;
                    line-height: 1.4;
                    color: #000;
                    font-weight: 600;
                }

                /* Shop Section */
                .shop {
                    padding: 3rem 0;
                    background-color: white;
                }

                .shop .section-title {
                    color: #000;
                }

                .shop-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
                    gap: 2rem;
                    margin-bottom: 3rem;
                }

                .product-card {
                    background-color: #fff;
                    border: 1px solid white;
                    border-radius: 8px;
                    overflow: hidden;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                }

                .product-card:hover {
                    transform: translateY(-4px);

                }

                .product-image {
                    width: 100%;
                    height:400px;
                    background-color: white;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #999;
                    position: relative;
                    border-bottom: 1px solid white;
                    font-size: 0.85rem;
                }
                .product-image img {
                    width:100%;
                    height:100%;
                    object-fit:cover;
                    object-position: center;
                }

                .hot-badge {
                    position: absolute;
                    top: 12px;
                    left: 12px;
                    background-color: white;
                    color: black;
                    padding: 0.3rem 0.7rem;
                    border-radius: 12px;
                    font-size: 0.75rem;
                    font-weight: 600;
                    text-transform: uppercase;
                }

                .product-info {
                    padding: 1.2rem;
                }

                .product-title {
                    font-size: 1rem;
                    margin-bottom: 0.7rem;
                    line-height: 1.4;
                    color: #000;
                    font-weight: 500;
                }

                .product-price {
                    color: #000;
                    font-weight: 600;
                    font-size: 1.1rem;
                }



                /* Brand Highlights */
                .brand-highlights {
                    padding: 3rem 0;
                    background-color: white;
                }

                .brand-highlights .section-title {
                    color: #000;
                    text-align: center;
                }

                .brand-subtitle {
                    color: #666;
                    text-align: center;
                    margin-bottom: 2rem;
                    font-size: 1rem;
                }

                .highlights-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
                    gap: 2rem;
                    margin-top: 2rem;
                }

                .highlight-card {
                    background-color: transparent;
                    border: none;
                    border-radius: 0;
                    padding: 0;
                    flex-direction: column;
                    display:flex;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                }

                .highlight-card:hover {
                    transform: translateY(-4px);
                    box-shadow: 0 4px 16px rgba(0,0,0,0.15);
                }

                .highlight-icon {
                    width: 100%;
                    aspect-ratio: 1/1;
                    overflow: hidden;
                    display: block;
                    margin: 0;


                }
                .highlight-icon img {
                  width: 100%;
                  height: 100%;
               object-fit: cover;  /* or cover if you want to fill the box */
                 object-position:center;
                 display: block;
}

                /* Footer */
                .footer {
                    background-color: black;
                    padding: 3rem 0 1rem;
                    color: black;
                }

                .footer-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .footer-nav {
                    display: flex;
                    flex-direction: column;
                    background:black;
                    gap: 1rem;
                    margin-bottom: 2rem;
                    align-items: center;
                }

                .footer-nav .nav-link1 {
                    color: #ccc;
                    font-size: 0.9rem;
                    padding: 0.7rem 0;
                    width: 100%;
                    text-align: center;
                    background-color: black;
                    transition: color 0.3s, background-color 0.3s;
                    text-decoration: none;
                }

                .footer-nav .nav-link1:hover {
                    color: #fff;
                    background-color: black;
                    text-decoration: none;
                }

                .social-icons {
                    display: flex;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 2rem;
                }

                .social-icon {
                    width: 40px;
                    height: 40px;
                    background-color: #333;
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #fff;
                    text-decoration: none;
                    transition: background-color 0.3s;
                    font-size: 1.1rem;
                    background-color: black;
                }

                .social-icon:hover {
                    background-color: #555;
                }

                .footer-text {
                    color: #666;
                    font-size: 0.85rem;
                    text-align: center;
                }
                a {
                    text-decoration:none;
                }
                /* Responsive Design */
                @media (min-width: 768px) {
                    .mobile-menu-btn {
                        display: none;
                    }

                    .desktop-nav, .header-right {
                        display: flex;
                    }

                    .tickets-btn {
                        position: absolute; /* Absolute for desktop*/
                        top: 2rem; /* Position from top*/
                        right: 8.8rem; /* Position from right*/
                        margin-top: 0; /* Reset mobile margin */
                    }

                    .hero-content {
                        padding: 0 2rem;
                    }

                    .footer-nav {
                        flex-direction: row;
                        justify-content: center;
                        flex-wrap: wrap;
                        gap: 2.5rem;
                        background-color: black;
                    }

                    .footer-nav .nav-link1 {
                        width: auto;
                        border-bottom: none;
                        padding: 0.5rem 0;
                        background-color: black;
                        text-decoration: none;

                    }

                    .footer-nav .nav-link1:hover {
                        background-color: black;
                        color: #black;
                        text-decoration: none;
                    }

                }


                @media (min-width: 1024px) {
                    .news-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }

                    .shop-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }

                    .highlights-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }
                        .highlights-grid {
            /* Adjust grid template columns if 200px icon size affects overall layout */
            /* You might need to make the columns larger or fewer if the 200px icons are too big */
            grid-template-columns: repeat(auto-fit, minmax(220px, 1fr)); /* Example adjustment for min-width for larger icons */
            gap: 1.5rem; /* Adjust gap as needed */
        }
                }
            `}</style>

            <div className="container">
                {/* Discount Banner */}
                {isDiscountVisible && (
    <div className="discount-banner">
        <span className="discount-icon"><img src="ticket-percent2.png"/></span>
        10% off store—Limited time!
        <button 
            className="discount-close"
            onClick={() => setIsDiscountVisible(false)}
        >
            ✕
        </button>
    </div>
)}

                {/* Header */}
                <header className="header">
                    <div className="header-content">
                        <button 
                            className="mobile-menu-btn"
                            onClick={() => setIsMobileMenuOpen(true)}
                        >
                            <MenuIcon />
                        </button>
                        <p className="logo">BURNITDOWNYT</p>

                        <NavLinks />

                        <div className="header-right">
                            <div className="header-icon">
                                <UserIcon />
                            </div>
                            <div className="header-icon">
                                <ShoppingBagIcon />
                            </div>
                        </div>


                    </div>
                </header>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <div className="mobile-menu-header">
                            <span className="logo">BURNITDOWNYT</span>
                            <button 
                                className="close-btn"
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                <CloseIcon />
                            </button>
                        </div>
                        <NavLinks isMobile={true} onItemClick={scrollToSection} />
                    </div>
                )}




                {/* Footer */}
                <footer className="footer">
                    <div className="footer-content">
                        <nav className="footer-nav">
                            <button onClick={() => scrollToSection(homeRef)} className="nav-link1">Home</button>
                            <button onClick={() => scrollToSection(shopRef)} className="nav-link1">Shop</button>
                            <a href=""><button className="nav-link1">2K Community</button></a>
                            <button onClick={handleMediaKitDownload} className="nav-link1">Media kit</button>
                            <button onClick={() => console.log('Contact Us clicked')} className="nav-link1">Contact Us</button>
                        </nav>

                        <div className="social-icons">
                            <a href="" className="social-icon"><img src="instagram1.png"/></a>
                            <a href="" className="social-icon"><img src="facebook1.png"/></a>
                            <a href="" className="social-icon"><img src="youtube1.png"/></a>
                        </div>

                        <p className="footer-text">© 2025 BURNITDOWNYT. All rights reserved.</p>
                    </div>
                </footer>
            </div>
        </>
    );
};

export default Product;
