import { useState, useRef } from 'react';

const Contact= () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);
    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = 'Media-kit.pdf';
        link.download = 'MediaKit.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setIsMobileMenuOpen(false);
    };
    const homeRef       = useRef(null);
    const shopRef       = useRef(null);
    const latestNewsRef = useRef(null);
    
  const scrollToSection = (ref) => {
     if (ref?.current) {
    ref.current.scrollIntoView({ behavior: 'smooth' });
    setIsMobileMenuOpen(false);
  }
};

    // Safe icon components using Unicode/HTML entities
    const MenuIcon = () => <span style={{fontSize: '20px'}}>☰</span>;
    const CloseIcon = () => <span style={{fontSize: '20px'}}>✕</span>;
    const UserIcon = () => <span style={{fontSize: '18px'}}><img src="user.png"/></span>;
    const ShoppingBagIcon = () => <span style={{fontSize: '18px'}}><img src="shoppingbag.png"/></span>;

    const NavLinks = ({ isMobile = false, onItemClick }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
            <button onClick={() => onItemClick ? onItemClick(homeRef) : scrollToSection(homeRef)} className="nav-link">
                Home
            </button>
            <button onClick={() => onItemClick ? onItemClick(shopRef) : scrollToSection(shopRef)} className="nav-link">
                Shop
            </button>
            <button onClick={() => onItemClick ? onItemClick(latestNewsRef) : scrollToSection(latestNewsRef)} className="nav-link">
                Latest News
            </button>
            <button onClick={() => console.log('Contact Us clicked')} className="nav-link">
                Contact Us
            </button>
        </nav>
    );  

    return (
        <>
            <style>{` * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                body {
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    background-color: #000;
                    color: white;
                    line-height: 1.6;
                }

                .container {
                    min-height: 100vh;
                    background-color: white;
                }
                
                /* Header Styles */
                .header {
                    background-color: white;
                    border-bottom: 1px solid #e0e0e0;
                    padding: 0.8rem 0;
                    position: sticky;
                    top: 0;
                    z-index: 1000;
                }

                .header-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    background-color: white;
                    position: relative;
                }

                 .desktop-nav {
                    display: none;
                    gap: 2.5rem;
                    background-color: white;
                    position: absolute;
                    left: 50%;
                    transform: translateX(-50%);
                      z-index: 10;
                }

                .nav-link {
                    background: none;
                    border: none;
                    color: Black;
                    background-color: white;
                    text-decoration: none;
                    font-size: 0.9rem;
                    cursor: pointer;
                    padding: 0.5rem 0;
                    font-weight: 500;
                    transition: color 0.3s;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    white-space: nowrap;
                }
                .nav-link:hover {
                    color: #000;
                    background-color: white;
                }
                
                .nav-link1.no-underline {
                   text-decoration: none;
                
                }
                .header-right {
                    display: flex;
                    align-items: center;
                    gap: 0.2rem;
                    background-color: white;
                    flex: 1; /* Take up available space */
                    justify-content: flex-end;
                }

                .header-icon {
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                    border-radius: 4px;
                    transition: background-color 0.3s;
                }

                .header-icon:hover {
                    background-color: #f5f5f5;
                }

                .mobile-menu-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 10;
                    background-color: white;
                    z-index: 2000;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                }

                .mobile-menu-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 2rem;
                }

                .mobile-menu-header .logo {
                    color: white;
                }

                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem;
                    background-color: white;
                    color: white;
                }

                .mobile-nav .nav-link {
                    font-size: 1.1rem;
                    padding: 1rem 0;
                    border-bottom: white;
                    text-align: center;
                    color: black;
                }

                .close-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }
                    /* === Contact Main === */
.contact-main {
  background-color: white;
  color: white;
  padding: 3rem 1rem;
  max-width: 800px;
  margin: 0 auto;
}

.contact-title {
  text-align: center;
  font-size: 2.25rem;
  font-weight: 600;
  margin-bottom: 2rem;
  color: #000;
}

/* Info‑cards container */
.contact-info {
  background-color: white;
  color: black;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 1.5rem;
  margin-bottom: 3rem;
}

/* Individual card */
.info-card {
  background-color: #f2f4f6;
  border-radius: 8px;
  padding: 1.5rem 1rem;
  text-align: center;
}

.info-icon {
  font-size: 2rem;
  margin-bottom: 0.5rem;
  color: #333;
}

.info-heading {
  font-size: 0.85rem;
  letter-spacing: 1px;
  margin-bottom: 0.25rem;
  color: #555;
}

.info-text {
  font-size: 1rem;
  color: #222;
}

/* Contact form */
.contact-form {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
}

.contact-form label {
  display: flex;
  flex-direction: column;
  font-size: 0.75rem;
  color: #555;
}

.contact-form input,
.contact-form textarea {
  margin-top: 0.5rem;
  padding: 0.75rem 1rem;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
  color: #000;
  background-color: #fff;
}

.submit-btn {
  width: fit-content;
  margin: 0 auto;
  padding: 0.75rem 2rem;
  background-color: #000;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;
}

.submit-btn:hover {
  background-color: #333;
}

/* Responsive tweaks */
@media (max-width: 480px) {
  .contact-main {
    padding: 2rem 0.5rem;
  }
  .submit-btn {
    width: 100%;
  }
}

                /* Footer */
                .footer {
                    background-color: black;
                    padding: 3rem 0 1rem;
                    color: black;
                }

                .footer-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .footer-nav {
                    display: flex;
                    flex-direction: column;
                    background:black;
                    gap: 1rem;
                    margin-bottom: 2rem;
                    align-items: center;
                }

                .footer-nav .nav-link1 {
                    color: #ccc;
                    font-size: 0.9rem;
                    padding: 0.7rem 0;
                    width: 100%;
                    text-align: center;
                    background-color: black;
                    transition: color 0.3s, background-color 0.3s;
                    text-decoration: none;
                }

                .footer-nav .nav-link1:hover {
                    color: #fff;
                    background-color: black;
                    text-decoration: none;
                }

                .social-icons {
                    display: flex;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 2rem;
                }

                .social-icon {
                    width: 40px;
                    height: 40px;
                    background-color: #333;
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #fff;
                    text-decoration: none;
                    transition: background-color 0.3s;
                    font-size: 1.1rem;
                    background-color: black;
                }

                .social-icon:hover {
                    background-color: #555;
                }

                .footer-text {
                    color: #666;
                    font-size: 0.85rem;
                    text-align: center;
                }
                a {
                    text-decoration:none;
                }
                    @media (min-width: 768px) {
                    .mobile-menu-btn {
                        display: none;
                    }

                    .desktop-nav, .header-right {
                        display: flex;
                    }
                        .footer-nav {
                        flex-direction: row;
                        justify-content: center;
                        flex-wrap: wrap;
                        gap: 2.5rem;
                        background-color: black;
                    }

                    .footer-nav .nav-link1 {
                        width: auto;
                        border-bottom: none;
                        padding: 0.5rem 0;
                        background-color: black;
                        text-decoration: none;

                    }

                    .footer-nav .nav-link1:hover {
                        background-color: black;
                        color: #black;
                        text-decoration: none;
                    }
                   
                }
                

               `} </style> 
            <div>
                <div className="container">

                {/* Header */}
                <header className="header">
                    <div className="header-content">
                        <button 
                            className="mobile-menu-btn"
                            onClick={() => setIsMobileMenuOpen(true)}
                        >
                            <MenuIcon />
                        </button>
                        
                        <NavLinks />
                        
                        <div className="header-right">
                            <div className="header-icon">
                                <UserIcon />
                            </div>
                            <div className="header-icon">
                                <ShoppingBagIcon />
                            </div>
                        </div>

                        
                    </div>
                </header>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <div className="mobile-menu-header">
                            <span className="logo">BURNITDOWNYT</span>
                            <button 
                                className="close-btn"
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                <CloseIcon />
                            </button>
                        </div>
                        <NavLinks isMobile={true} onItemClick={scrollToSection} />
                    </div>
                )}
          <main className="contact-main">
  <h1 className="contact-title">Contact Us</h1>

  {/* === Info Cards === */}
  <div className="contact-info">
    <div className="info-card">
      <div className="info-icon"><img src="address.png"/></div>
      <h2 className="info-heading">ADDRESS</h2>
      <p className="info-text">1234 WWE Blvd, Las Vegas, NV</p>
    </div>
    <div className="info-card">
      <div className="info-icon"><img src="phone.png"/></div>
      <h2 className="info-heading">CONTACT US</h2>
      <p className="info-text">+1 (555) 123‑4567</p>
    </div>
    <div className="info-card">
      <div className="info-icon"><img src="email.png"/></div>
      <h2 className="info-heading">EMAIL</h2>
      <p className="info-text">info@burnitdownyt.com</p>
    </div>
  </div>

  {/* === Contact Form === */}
  <form
    className="contact-form"
    onSubmit={(e) => {
      e.preventDefault();
      alert('Your message has been sent!');
    }}
  >
    <label>
      FULL NAME
      <input type="text" placeholder="Your Name" required />
    </label>
    <label>
      EMAIL ADDRESS
      <input type="email" placeholder="Your Email" required />
    </label>
    <label>
      MESSAGE
      <textarea rows="5" placeholder="Your message" required />
    </label>
    <button type="submit" className="submit-btn">Send Message</button>
  </form>
</main>

             <footer className="footer">
                    <div className="footer-content">
                        <nav className="footer-nav">
                            <button onClick={() => scrollToSection(homeRef)} className="nav-link1">Home</button>
                            <button onClick={() => scrollToSection(shopRef)} className="nav-link1">Shop</button>
                            <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr"><button className="nav-link1">2K Community</button></a>
                            <button onClick={handleMediaKitDownload} className="nav-link1">Media kit</button>
                            <button onClick={() => console.log('Contact Us clicked')} className="nav-link1">Contact Us</button>
                        </nav>
                        
                        <div className="social-icons">
                            <a href="https://www.instagram.com/burnitdownyt?igsh=MTExOGNwOHJhZWYyYQ%3D%3D&utm_source=qr" className="social-icon"><img src="instagram1.png"/></a>
                            <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr" className="social-icon"><img src="facebook1.png"/></a>
                            <a href="https://youtube.com/@burnitdownyt?si=ogbNfYLps54zRZQ1" className="social-icon"><img src="youtube1.png"/></a>
                        </div>
                        
                        <p className="footer-text">© 2025 BURNITDOWNYT. All rights reserved.</p>
                    </div>
                </footer>
            </div>
            </div>
</> 
            )
}

export default Contact;
