import { useState, useRef } from 'react';

const Product = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);
    const [quantity, setQuantity] = useState(1);
    const [isWishlisted, setIsWishlisted] = useState(false);
    const [selectedImageIndex, setSelectedImageIndex] = useState(0);
    const [showAdditionalInfo, setShowAdditionalInfo] = useState(false);
    const scrollRef = useRef(null);

    // Sample product data
    const product = {
        name: "WWE Undisputed Championship with John Cena side plates",
        price: 2199.00,
        originalPrice: 2400.00,
        images: [
            "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=500&h=500&fit=crop",
            "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=500&h=500&fit=crop",
            "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=500&h=500&fit=crop"
        ],
        description: "You can use the removable tray for serving. The design makes it easy to put the tray back after use since you place it directly on the table frame without having to fit it into any holes."
    };

    const relatedProducts = [
        {
            id: 1,
            name: "WWE Winged Eagle Championship Belt",
            price: 1799.00,
            originalPrice: 1900.00,
            image: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300&h=200&fit=crop",
            badge: "NEW",
            discount: "-10%"
        },
        {
            id: 2,
            name: "WWE Big Gold World Heavyweight Championship", 
            price: 1799.00,
            image: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300&h=200&fit=crop"
        },
        {
            id: 3,
            name: "WWE New World Heavyweight Championship Belt",
            price: 1999.00,
            image: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300&h=200&fit=crop"
        },
        {
            id: 4,
            name: "WWE New NXT Championship",
            price: 1199.00,
            image: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300&h=200&fit=crop"
        }
    ];

    const handleQuantityChange = (change) => {
        setQuantity(Math.max(1, quantity + change));
    };

    const scrollProducts = (direction) => {
        const container = scrollRef.current;
        if (container) {
            const scrollAmount = 300;
            container.scrollBy({
                left: direction === 'left' ? -scrollAmount : scrollAmount,
                behavior: 'smooth'
            });
        }
    };

    const navigateToShop = () => {
        console.log('Navigate to shop section');
    };

    return (
        <div className="min-h-screen bg-black text-white">
            {/* Discount Banner */}
            {isDiscountVisible && (
                <div className="bg-gray-300 text-black text-center py-2 px-4 text-sm font-medium flex items-center justify-center gap-2 relative">
                    <span className="text-lg">ðŸŽ«</span>
                    10% off storeâ€”Limited time!
                    <button 
                        className="absolute right-4 text-black hover:text-gray-600 text-lg font-bold"
                        onClick={() => setIsDiscountVisible(false)}
                    >
                        âœ•
                    </button>
                </div>
            )}

            {/* Header */}
            <header className="bg-white border-b border-gray-200 py-3 sticky top-0 z-50">
                <div className="max-w-7xl mx-auto px-4 flex justify-between items-center">
                    {/* Mobile Menu Button */}
                    <button 
                        className="md:hidden text-black text-xl p-2"
                        onClick={() => setIsMobileMenuOpen(true)}
                    >
                        â˜°
                    </button>

                    {/* Logo */}
                    <div className="text-black text-xl font-bold flex-1 md:flex-none text-center md:text-left">
                        BURNITDOWNYT
                    </div>

                    {/* Desktop Navigation */}
                    <nav className="hidden md:flex space-x-8 absolute left-1/2 transform -translate-x-1/2">
                        <a href="#" className="text-black hover:text-gray-600 font-medium">Home</a>
                        <a href="#" className="text-black hover:text-gray-600 font-medium">Shop</a>
                        <a href="#" className="text-black hover:text-gray-600 font-medium">2K Community</a>
                        <a href="#" className="text-black hover:text-gray-600 font-medium">Media kit</a>
                        <a href="#" className="text-black hover:text-gray-600 font-medium">Contact Us</a>
                    </nav>

                    {/* Header Icons */}
                    <div className="flex items-center space-x-2">
                        <button className="text-black p-2 hover:bg-gray-100 rounded">
                            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                                <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                            </svg>
                        </button>
                        <button className="text-black p-2 hover:bg-gray-100 rounded relative">
                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                            </svg>
                            <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">2</span>
                        </button>
                    </div>
                </div>
            </header>

            {/* Mobile Menu */}
            {isMobileMenuOpen && (
                <div className="fixed inset-0 bg-white z-50 md:hidden">
                    <div className="p-6">
                        <div className="flex justify-between items-center mb-8">
                            <span className="text-black text-xl font-bold">BURNITDOWNYT</span>
                            <button 
                                className="text-black text-xl"
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                âœ•
                            </button>
                        </div>

                        <nav className="flex flex-col space-y-6">
                            <a href="#" className="text-black text-lg py-3 border-b border-gray-200">Home</a>
                            <a href="#" className="text-black text-lg py-3 border-b border-gray-200">Shop</a>
                            <a href="#" className="text-black text-lg py-3 border-b border-gray-200">2K Community</a>
                            <a href="#" className="text-black text-lg py-3 border-b border-gray-200">Media kit</a>
                            <a href="#" className="text-black text-lg py-3 border-b border-gray-200">Contact Us</a>

                            {/* User Icon in Mobile Menu */}
                            <button className="flex items-center text-black text-lg py-3 border-b border-gray-200">
                                <svg className="w-5 h-5 mr-3" fill="currentColor" viewBox="0 0 20 20">
                                    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                                </svg>
                                Account
                            </button>
                        </nav>
                    </div>
                </div>
            )}

            {/* Main Content */}
            <main className="max-w-7xl mx-auto px-4 py-10">
                {/* Product Section */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 mb-20">
                    {/* Product Images */}
                    <div className="space-y-4">
                        {/* Main Image */}
                        <div className="relative bg-white rounded-lg overflow-hidden">
                            <img 
                                src={product.images[selectedImageIndex]} 
                                alt={product.name}
                                className="w-full h-96 lg:h-[500px] object-cover"
                            />
                            <div className="absolute top-4 left-4 bg-green-500 text-white px-3 py-1 rounded text-sm font-semibold">
                                NEW
                            </div>
                            <div className="absolute top-4 right-4 bg-red-500 text-white px-3 py-1 rounded text-sm font-semibold">
                                -10%
                            </div>
                            <div className="absolute bottom-4 right-4 bg-black bg-opacity-80 text-white p-6 rounded text-center font-bold">
                                CONTACT<br/>FOR<br/>MORE<br/>IMAGES
                            </div>
                        </div>

                        {/* Thumbnail Images */}
                        <div className="flex justify-center space-x-3">
                            {product.images.map((image, index) => (
                                <button
                                    key={index}
                                    className={`w-20 h-20 rounded-lg overflow-hidden border-2 ${
                                        selectedImageIndex === index ? 'border-gray-800' : 'border-transparent'
                                    }`}
                                    onClick={() => setSelectedImageIndex(index)}
                                >
                                    <img src={image} alt={`View ${index + 1}`} className="w-full h-full object-cover" />
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Product Info */}
                    <div className="space-y-6">
                        <h1 className="text-2xl lg:text-3xl font-semibold leading-tight">
                            {product.name}
                        </h1>

                        <p className="text-gray-400 leading-relaxed">
                            Buy one or buy a few and make every space where you sit more convenient. Light and easy to move around with removable tray top, handy for serving snacks.
                        </p>

                        <div className="flex items-baseline space-x-3">
                            <span className="text-3xl font-semibold">${product.price.toFixed(2)}</span>
                            <span className="text-xl text-gray-500 line-through">${product.originalPrice.toFixed(2)}</span>
                        </div>

                        {/* Quantity and Wishlist */}
                        <div className="flex items-center space-x-4">
                            <div className="flex items-center border border-gray-600 rounded">
                                <button 
                                    className="px-4 py-3 text-gray-400 hover:text-white hover:bg-gray-800"
                                    onClick={() => handleQuantityChange(-1)}
                                >
                                    âˆ’
                                </button>
                                <input 
                                    type="number" 
                                    value={quantity} 
                                    onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                                    className="w-16 py-3 text-center bg-transparent border-0 focus:outline-none"
                                />
                                <button 
                                    className="px-4 py-3 text-gray-400 hover:text-white hover:bg-gray-800"
                                    onClick={() => handleQuantityChange(1)}
                                >
                                    +
                                </button>
                            </div>

                            <button 
                                className={`flex items-center space-x-2 px-6 py-3 border rounded transition-all ${
                                    isWishlisted 
                                        ? 'bg-white text-black border-white' 
                                        : 'border-gray-600 text-gray-400 hover:border-white hover:text-white'
                                }`}
                                onClick={() => setIsWishlisted(!isWishlisted)}
                            >
                                <span>{isWishlisted ? 'â¤ï¸' : 'ðŸ¤'}</span>
                                <span>Wishlist</span>
                            </button>
                        </div>

                        {/* Add to Cart Button */}
                        <button className="w-full bg-white text-black py-4 rounded font-semibold hover:bg-gray-200 transition-colors">
                            Add to Cart
                        </button>

                        {/* Additional Info */}
                        <div className="border-t border-gray-800 pt-6">
                            <button 
                                className="flex items-center justify-between w-full text-left font-semibold"
                                onClick={() => setShowAdditionalInfo(!showAdditionalInfo)}
                            >
                                Additional Info
                                <span className="text-xl">{showAdditionalInfo ? 'âŒ„' : 'âŒƒ'}</span>
                            </button>

                            {showAdditionalInfo && (
                                <div className="mt-4 text-gray-400 leading-relaxed">
                                    <h4 className="font-semibold text-white mb-2">Details</h4>
                                    <p>{product.description}</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Related Products */}
                <section>
                    <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-semibold">You might also like</h2>
                        <button 
                            className="text-white hover:text-gray-300 underline flex items-center space-x-2"
                            onClick={navigateToShop}
                        >
                            <span>More Products</span>
                            <span>â†’</span>
                        </button>
                    </div>

                    <div className="relative">
                        <div 
                            ref={scrollRef}
                            className="flex space-x-5 overflow-x-auto scrollbar-hide pb-4"
                            style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
                        >
                            {relatedProducts.map((item) => (
                                <div key={item.id} className="flex-none w-72 bg-white rounded-lg overflow-hidden shadow-lg hover:shadow-xl transition-shadow cursor-pointer">
                                    <div className="relative">
                                        <img src={item.image} alt={item.name} className="w-full h-48 object-cover" />
                                        {item.badge && (
                                            <div className="absolute top-3 left-3 bg-green-500 text-white px-2 py-1 rounded text-xs font-semibold">
                                                {item.badge}
                                            </div>
                                        )}
                                        {item.discount && (
                                            <div className="absolute top-3 right-3 bg-red-500 text-white px-2 py-1 rounded text-xs font-semibold">
                                                {item.discount}
                                            </div>
                                        )}
                                    </div>
                                    <div className="p-4">
                                        <h3 className="text-black font-medium mb-2 leading-tight">{item.name}</h3>
                                        <div className="text-black font-semibold">
                                            ${item.price.toFixed(2)}
                                            {item.originalPrice && (
                                                <span className="text-gray-500 line-through text-sm ml-2">
                                                    ${item.originalPrice.toFixed(2)}
                                                </span>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>

                        {/* Scroll Controls */}
                        <div className="flex justify-center space-x-2 mt-8">
                            <button 
                                className="w-10 h-10 bg-white text-black rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                                onClick={() => scrollProducts('left')}
                            >
                                â€¹
                            </button>
                            <button 
                                className="w-10 h-10 bg-white text-black rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                                onClick={() => scrollProducts('right')}
                            >
                                â€º
                            </button>
                        </div>
                    </div>
                </section>
            </main>

            {/* Footer */}
            <footer className="bg-black border-t border-gray-800 py-12">
                <div className="max-w-7xl mx-auto px-4">
                    <nav className="flex flex-col md:flex-row justify-center items-center space-y-4 md:space-y-0 md:space-x-8 mb-8">
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">Home</a>
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">Shop</a>
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">2K Community</a>
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">Media kit</a>
                        <a href="#" className="text-gray-400 hover:text-white transition-colors">Contact Us</a>
                    </nav>

                    <div className="flex justify-center space-x-4 mb-8">
                        <a href="#" className="w-10 h-10 bg-gray-800 rounded-full flex items-center justify-center text-white hover:bg-gray-700 transition-colors">
                            ðŸ“·
                        </a>
                        <a href="#" className="w-10 h-10 bg-gray-800 rounded-full flex items-center justify-center text-white hover:bg-gray-700 transition-colors">
                            ðŸ“˜
                        </a>
                        <a href="#" className="w-10 h-10 bg-gray-800 rounded-full flex items-center justify-center text-white hover:bg-gray-700 transition-colors">
                            ðŸ“º
                        </a>
                    </div>

                    <p className="text-center text-gray-500 text-sm">
                        Â© 2025 BURNITDOWNYT. All rights reserved.
                    </p>
                </div>
            </footer>

            <style jsx>{`
                .scrollbar-hide::-webkit-scrollbar {
                    display: none;
                }
            `}</style>
        </div>
    );
};

export default Product;
