import { useState, useRef } from 'react';

const Contact = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isDiscountVisible, setIsDiscountVisible] = useState(true);

  const homeRef = useRef(null);
  const shopRef = useRef(null);
  const latestNewsRef = useRef(null);

  const handleMediaKitDownload = () => {
    const link = document.createElement('a');
    link.href = 'Media-kit.pdf';
    link.download = 'MediaKit.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    setIsMobileMenuOpen(false);
  };

  // Icon components
  const MenuIcon = () => <span style={{ fontSize: '20px' }}>☰</span>;
  const CloseIcon = () => <span style={{ fontSize: '20px' }}>✕</span>;
  const UserIcon = () => <img src="Vector.png" alt="User" style={{ width: '20px' }} />;
  const ShoppingBagIcon = () => <img src="shoppingbag.png" alt="Bag" style={{ width: '20px' }} />;

  const scrollToSection = (ref) => {
    if (ref?.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
      setIsMobileMenuOpen(false);
    }
  };

  const NavLinks = ({ isMobile = false, onItemClick }) => (
    <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
      <button onClick={() => onItemClick(homeRef)} className="nav-link">Home</button>
      <button onClick={() => onItemClick(shopRef)} className="nav-link">→Shop</button>
      <button onClick={() => onItemClick(latestNewsRef)} className="nav-link">
        <img src="arrow-right.png" alt="arrow" /> Latest News
      </button>
      <button onClick={handleMediaKitDownload} className="nav-link">Media Kit</button>
      <button onClick={() => console.log('Contact Us clicked')} className="nav-link">Contact Us</button>
    </nav>
  );

  return (
    <>
      <style>{`
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body {
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
          background-color: #000;
          color: white;
          line-height: 1.6;
        }
        .container { min-height: 100vh; background-color: #000; }
        .header { background-color: white; border-bottom: 1px solid #e0e0e0; padding: 0.8rem 0; position: sticky; top: 0; z-index: 1000; }
        .header-content {
          max-width: 1400px; margin: 0 auto; padding: 0 1rem;
          display: flex; justify-content: space-between; align-items: center; background-color: white;
        }
        .desktop-nav { display: none; }
        .nav-link {
          background: none; border: none; color: black; font-size: 0.9rem;
          cursor: pointer; padding: 0.5rem 0; font-weight: 500;
          display: inline-flex; align-items: center; gap: 0.25rem;
          background-color: white; white-space: nowrap;
        }
        .nav-link:hover { color: #000; }
        .header-right {
          display: flex; align-items: center; gap: 0.2rem; background-color: white; flex: 1; justify-content: flex-end;
        }
        .header-icon {
          cursor: pointer; padding: 0.5rem; border-radius: 4px; transition: background-color 0.3s;
        }
        .header-icon:hover { background-color: #f5f5f5; }
        .mobile-menu-btn {
          background: none; border: none; color: #333; cursor: pointer; padding: 0.5rem;
        }
        .mobile-menu {
          position: fixed; top: 0; left: 0; right: 0; bottom: 0;
          background-color: white; z-index: 2000; padding: 2rem;
          display: flex; flex-direction: column;
        }
        .mobile-menu-header {
          display: flex; justify-content: space-between; align-items: center; margin-bottom: 2rem;
        }
        .mobile-nav {
          display: flex; flex-direction: column; gap: 1.5rem; background-color: white;
        }
        .mobile-nav .nav-link {
          font-size: 1.1rem; padding: 1rem 0; text-align: center; color: black;
        }
        .close-btn {
          background: none; border: none; color: #333; cursor: pointer; padding: 0.5rem;
        }
        .footer {
          background-color: black; padding: 3rem 0 1rem; color: white;
        }
        .footer-content {
          max-width: 1400px; margin: 0 auto; padding: 0 1rem;
        }
        .footer-nav {
          display: flex; flex-direction: column; gap: 1rem; margin-bottom: 2rem; align-items: center;
        }
        .footer-nav .nav-link1 {
          color: #ccc; font-size: 0.9rem; padding: 0.7rem 0;
          width: 100%; text-align: center; background-color: black;
          transition: color 0.3s, background-color 0.3s;
        }
        .footer-nav .nav-link1:hover { color: #fff; background-color: black; }
        .social-icons {
          display: flex; justify-content: center; gap: 1rem; margin-bottom: 2rem;
        }
        .social-icon {
          width: 40px; height: 40px; background-color: black;
          border-radius: 50%; display: flex; align-items: center; justify-content: center;
          color: white; font-size: 1.1rem;
        }
        .social-icon:hover { background-color: #555; }
        .footer-text {
          color: #666; font-size: 0.85rem; text-align: center;
        }
        a { text-decoration: none; }
      `}</style>

      <div className="container">
        {/* Discount Banner */}
        {isDiscountVisible && (
          <div className="discount-banner">
            <span className="discount-icon">
              <img src="ticket-percent.png" alt="discount" />
            </span>
            10% off store—Limited time!
            <button className="discount-close" onClick={() => setIsDiscountVisible(false)}>✕</button>
          </div>
        )}

        {/* Header */}
        <header className="header">
          <div className="header-content">
            <button className="mobile-menu-btn" onClick={() => setIsMobileMenuOpen(true)}>
              <MenuIcon />
            </button>
            <p className="logo">BURNITDOWNYT</p>
            <NavLinks />
            <div className="header-right">
              <div className="header-icon"><UserIcon /></div>
              <div className="header-icon"><ShoppingBagIcon /></div>
            </div>
          </div>
        </header>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="mobile-menu">
            <div className="mobile-menu-header">
              <span className="logo">BURNITDOWNYT</span>
              <button className="close-btn" onClick={() => setIsMobileMenuOpen(false)}><CloseIcon /></button>
            </div>
            <NavLinks isMobile={true} onItemClick={scrollToSection} />
          </div>
        )}

        {/* Footer */}
        <footer className="footer">
          <div className="footer-content">
            <nav className="footer-nav">
              <button onClick={() => scrollToSection(homeRef)} className="nav-link1">Home</button>
              <button onClick={() => scrollToSection(shopRef)} className="nav-link1">Shop</button>
              <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr">
                <button className="nav-link1">2K Community</button>
              </a>
              <button onClick={handleMediaKitDownload} className="nav-link1">Media kit</button>
              <button onClick={() => console.log('Contact Us clicked')} className="nav-link1">Contact Us</button>
