                    display: flex;
                    align-items: baseline;
                    gap: 1rem;
                }

                .current-price {
                    font-size: 1.8rem;
                    font-weight: 500;
                    color: #111827;
                }

                .original-price {
                    font-size: 1.2rem;
                    color: #9ca3af;
                    text-decoration: line-through;
                }

                /* --- Actions: Quantity, Wishlist, Add to Cart --- */
                .actions-row {
                    display: flex;
                    flex-direction: column;
                    gap: 1rem;
                    margin-bottom: 1.5rem;
                }

                @media (min-width: 768px) {
                    .actions-row {
                        flex-direction: row;
                    }
                }

                .quantity-controls {
                    display: flex;
                    align-items: center;
                    border: 1px solid #d1d5db;
                    border-radius: 8px;
                    overflow: hidden;
                }

                .quantity-btn {
                    background: #fff;
                    border: none;
                    padding: 12px 16px;
                    cursor: pointer;
                    font-size: 1rem;
                    color: #4b5563;
                }

                .quantity-btn:hover {
                    background: #f9fafb;
                }

                .quantity-input {
                    border: none;
                    border-left: 1px solid #d1d5db;
                    border-right: 1px solid #d1d5db;
                    padding: 12px;
                    text-align: center;
                    width: 60px;
                    font-size: 1rem;
                    font-weight: 500;
                    -moz-appearance: textfield; /* Firefox */
                }
                .quantity-input::-webkit-outer-spin-button,
                .quantity-input::-webkit-inner-spin-button {
                    -webkit-appearance: none;
                    margin: 0;
                }

                .wishlist-btn {
                    background: #fff;
                    border: 1px solid #d1d5db;
                    padding: 12px 20px;
                    border-radius: 8px;
                    cursor: pointer;
                    transition: all 0.2s;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 8px;
                    font-weight: bold;
                    flex-grow: 1;
                }

                .wishlist-btn.active, .wishlist-btn:hover {
                    border-color: #111827;
                    background: #f9fafb;
                }

                .wishlist-btn .heart {
                    color: ${isWishlisted ? '#ef4444' : '#9ca3af'};
                    transition: color 0.2s;
                }

                .add-to-cart-btn {
                    background: #111827;
                    color: #fff;
                    border: none;
                    padding: 16px 24px;
                    border-radius: 8px;
                    font-size: 1rem;
                    font-weight: 500;
                    cursor: pointer;
                    width: 100%;
                    transition: background 0.2s;
                }

                .add-to-cart-btn:hover {
                    background: #374151;
                }

                /* --- Additional Info --- */
                .additional-info {
                    border-top: 1px solid #e5e7eb;
                    margin-top: 24px;
                }

                .info-toggle {
                    background: none;
                    border: none;
                    width: 100%;
                    text-align: left;
                    font-size: 1rem;
                    font-weight: 500;
                    padding: 16px 0;
                    cursor: pointer;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }

                .info-content {
                    padding-bottom: 16px;
                    color: #4b5563;
                }

                /* --- Related Products Section --- */
                .related-products {
                    margin-top: 80px;
                }

                .section-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 32px;
                }

                .section-title {
                    font-size: 1.75rem;
                    font-weight: 600;
                }

                .more-products-link {
                    color: #111827;
                    font-size: 1rem;
                    font-weight: 500;
                    text-decoration: none;
                    transition: color 0.2s;
                }

                .more-products-link:hover {
                    color: #4b5563;
                }

                .products-grid {
                    display: grid;
                    grid-template-columns: repeat(2, 1fr); /* 2 columns on mobile */
                    gap: 1.5rem;
                }

                @media (min-width: 1024px) {
                    .products-grid {
                        grid-template-columns: repeat(4, 1fr); /* 4 columns on desktop */
                    }
                }

                .related-product-card {
                    background: #fff;
                    border-radius: 8px;
                    overflow: hidden;
                    transition: transform 0.2s;
                    text-decoration: none;
                    color: inherit;
                    display: block;
                }

                .related-product-card:hover {
                    transform: translateY(-4px);
                }

                .related-product-card img {
                    width: 100%;
                    height: 250px;
                    object-fit: cover;
                    background-color: #f3f4f6;
                }

                .related-card-content {
                    padding: 12px 4px;
                }

                .related-card-title {
                    font-size: 1rem;
                    font-weight: 500;
                    margin-bottom: 4px;
                }

                .related-card-price {
                    font-weight: 500;
                    font-size: 1rem;
                }

                .related-card-original-price {
                    color: #9ca3af;
                    text-decoration: line-through;
                    font-size: 0.9rem;
                    margin-left: 8px;
                }



                }
