import { useState, useRef } from 'react';

const Product = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);
    // Refs for scrolling to sections
    const homeRef = useRef(null);
    const shopRef = useRef(null);
    const latestNewsRef = useRef(null);
    const scrollRef = useRef(null);
    const scrollProducts = (direction) => {
        const container = scrollRef.current;
        if (container) {
            const scrollAmount = 300;
            container.scrollBy({
                left: direction === 'left' ? -scrollAmount : scrollAmount,
                behavior: 'smooth'
            });
        }
    };
    const scrollToSection = (ref) => {
        ref.current?.scrollIntoView({ behavior: 'smooth' });
        setIsMobileMenuOpen(false);
    };
    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = 'Media-kit.pdf';
        link.download = 'MediaKit.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setIsMobileMenuOpen(false);
    };

    // Safe icon components using Unicode/HTML entities
    const MenuIcon = () => <span style={{fontSize: '20px'}}></span>;
    const CloseIcon = () => <span style={{fontSize: '20px'}}></span>;
    const UserIcon = ({ isMobile = false }) => (<button
    style={{
      background: 'none',
      border: 'none',
      padding: 0,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      filter: isMobile ? 'invert(1)' : 'none',
    }}
    aria-label="User"
  >
    <img src="user.png" alt="User Icon" style={{ width: '20px' }} />
  </button>
);
    const ShoppingBagIcon = () => (
  <button
    style={{
      background: 'none',
      border: 'none',
      padding: 0,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}
    aria-label="Shopping Bag"
  >
    <img src="shoppingbag.png" alt="Shopping Icon" style={{ width: '20px' }} />
  </button>
);

    // State for quantity, wishlist, etc.
    const [quantity, setQuantity] = useState(1);
    const [isWishlisted, setIsWishlisted] = useState(false);
    const [showAdditionalInfo, setShowAdditionalInfo] = useState(true);

    // --- Product Data ---
    // This data can come from props, context, or an API call.
    const product = {
        id: 'wwe-undisputed-cena',
        name: "Tray Table", // Updated name to match the example image
        price: 199.00,
        originalPrice: 400.00,
        images: [
            "WWEBELTCENA.png",
            "contactformoreimages.png"
        ],
        description: "Buy one or buy a few and make every space where you sit more convenient. Light and easy to move around with removable tray top, handy for serving snacks."
    };

    // --- Related Products Data ---
    const relatedProducts = [
        {
            id: 1,
            name: "Loveseat Sofa", // Updated name to match the example image
            price: 199.00,
            originalPrice: 400.00,
            image: "WIGNEDEAGLE_.png",
            badge: "NEW",
            discount: "-10%"
        },
        {
            id: 2,
            name: "Table Lamp", // Updated name to match the example image
            price: 24.99,
            image: "WORLDTITLEE.png"
        },
        {
            id: 3,
            name: "Beige Table Lamp", // Updated name to match the example image
            price: 24.99,
            image: "WORLDTITLE.png"
        },
        {
            id: 4,
            name: "Bamboo basket", // Updated name to match the example image
            price: 24.99,
            image: "NXTTITLE.png"
        },
    ];
     const NavLinks = ({ isMobile = false, onItemClick }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
             {isMobile && (
              <div className="mobile-user-icon">
            <UserIcon/>
            </div>
            )}</nav>);
    // --- Event Handlers ---
    const handleQuantityChange = (change) => {
        setQuantity(prev => Math.max(1, prev + change));
    };

    // This function simulates navigation. Replace with your actual routing logic.
    const navigateToProduct = (productId) => {
        // In a real app, you'd use something like:
        // history.push(`/product/${productId}`);
        alert(`Navigating to product with ID: ${productId}`);
    };
    return (
        <>
            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                body {
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    background-color: white;
                    color: black;
                    line-height: 1.6;
                }

                .container {
                    min-height: 100vh;
                    background-color: white;
                }

                /* Discount Banner */
                .discount-banner {
                    background-color: #D3D3D3;
                    color: black;
                    text-align: center;
                    padding: 0.5rem;
                    font-size: 0.85rem;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 0.5rem;
                    position: relative;
                }

                .discount-banner .discount-icon {
                    display:flex;
                    align-items:center;

            }
                .discount-close {
                    position: absolute;
                    right: 1rem;
                    background: none;
                    border: none;
                    color: white;
                    cursor: pointer;
                    font-size: 18px;
                    padding: 0.2rem;
                    display: flex;
                    align-items: center;
                    justify-content: center;
}

.discount-close:hover {
    color: #ccc;
}                 

                .discount-banner .shop-show {
                    color: $fff;
                    text-decoration: none;
                    font-weight: 600;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;


                }
                .discount-banner .shop-now img {
                    width: 1rem;
                    height: auto;
                }

                /* Header Styles */
                .header {
                    background-color: white;
                    border-bottom: 1px solid #e0e0e0;
                    padding: 0.8rem 0;
                    position: sticky;
                    top: 0;
                    z-index: 1000;
                }

                .header-content {
                    max-width: 1200px;
                    margin: 0 auto;
                    display: flex;

                    justify-content: space-between;
                    align-items: center;
                    padding: 0 1rem;
                    background-color: white;
                    position: relative;
                }


                .logo {
                    font-size: 1.4rem;
                    font-weight: bold;
                    color: #000;
                    text-decoration: none;
                    background-color: white;
                    flex: 1;
                    margin-left: 0.5rem;

                }

                .desktop-nav {
                    display: none;
                    gap: 2rem;
                }

                .nav-link {
                    background: none;
                    border: none;
                    color: Black;
                    background-color: white;
                    text-decoration: none;
                    font-size: 0.9rem;
                    cursor: pointer;
                    padding: 0.5rem 0;
                    font-weight: 500;
                    transition: color 0.3s;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    white-space: nowrap;
                }
                .nav-link img {
                   display: inline-block;
                   width: 1rem;
                   height: auto;
                   margin: 0;
                }
                .nav-arrow {
                   width: 1rem;
                   height:auto;
                   margin-right: 0.25rem;!important
                   display: inline-block;

                }


                .nav-link:hover {
                    color: #000;
                    background-color: white;
                }

                .nav-link1.no-underline {
                   text-decoration: none;

                }

                .header-right {
                    display: flex;
                    align-items: center;
                    gap: 0.2rem;
                    background-color: white;
                    flex: 1; /* Take up available space */
                    justify-content: flex-end;
                }
                    @media (max-width: 768px) {
  .header-right {
    display: none;
  }
}     

                .header-icon {
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                    border-radius: 4px;
                    transition: background-color 0.3s;
                }

                .header-icon:hover {
                    background-color: #f5f5f5;
                }
                    .desktop-only {
  display: block;
}

@media (max-width: 768px) {
  .desktop-only {
    display: none !important;
  }
}

                .mobile-menu-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 10;
                    background-color: white;
                    z-index: 2000;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                }

                .mobile-menu-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 2rem;
                }

                .mobile-menu-header .logo {
                    color: white;
                }

                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem;
                    background-color: white;
                    color: white;
                }

                .mobile-nav .nav-link {
                    font-size: 1.1rem;
                    padding: 1rem 0;
                    border-bottom: white;
                    text-align: center;
                    color: black;
                }

                .close-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                 .cart-badge {
                    position: absolute;
                    top: -8px;
                    right: -8px;
                    background: white;
                    color: white;
                    border-radius: 50%;
                    width: 18px;
                    height: 18px;
                    font-size: 12px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                /* Main Content */
                .main-content {
                    max-width: 1200px;
                    margin: 0 auto;
                    padding: 40px 20px;
                    background:white;
                }

                .product-container {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;

                }
                @media (min-width: 768px) {
                 .product-container {
                    grid-template-columns: 2fr 3fr;  /* desktop: thumbnails + main/info */
                    gap: 3rem;
                           }
                       }

                /* Product Images */
                .product-images {
                    display: flex;
                    gap: 16px;
                }

                .thumbnail-images {
                    display: flex;
                    justify content:center;
                    margin-bottom: 1rem;
                    gap: 1rem;
                }  
                @media (min-width: 768px) {
                .thumbnail-images {
                   flex-direction: column;
                   margin-bottom: 0;
                   }
                }

                .thumbnail {
                    width: 80px;
                    height: 80px;
                    border: 2px solid transparent;
                    border-radius: 8px;
                    overflow: hidden;
                    cursor: pointer;
                    transition: border-color 0.3s;
                }

                .thumbnail.active {
                    border-color: #333;
                }

                .thumbnail img {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;

                }

                .main-image {
                    flex: 1;
                    position: relative;
                }

                .main-image img {
                    width: 100%;
                    height: auto;
                    object-fit: cover;
                    border-radius: 8px;
                }

                .product-badge {
                    position: absolute;
                    top: 16px;
                    left: 16px;
                    background: white;
                    color: black;
                    padding: 6px 12px;
                    border-radius: 4px;
                    font-size: 12px;
                    font-weight: 600;
                }

                .discount-badge {
                    position: absolute;
                    top: 16px;
                    right: 16px;
                    background: green;
                    color: white;
                    padding: 6px 12px;
                    border-radius: 4px;
                    font-size: 12px;
                    font-weight: 600;
                }

                .contact-overlay {
                    position: absolute;
                    bottom: 16px;
                    right: 16px;
                    background: rgba(0, 0, 0, 0.8);
                    color: white;
                    padding: 40px;
                    border-radius: 8px;
                    text-align: center;
                    font-weight: bold;
                    font-size: 18px;
                }

                /* Product Info */
                .product-info h1 {
                    font-size: 32px;
                    font-weight: 600;
                    margin-bottom: 16px;
                    line-height: 1.3;
                }

                .product-description {
                    color: #666;
                    line-height: 1.6;
                    margin-bottom: 24px;
                }

                .price-section {
                    margin-bottom: 32px;
                }

                .current-price {
                    font-size: 28px;
                    font-weight: 600;
                    color: #333;
                }

                .original-price {
                    font-size: 20px;
                    color: #999;
                    text-decoration: line-through;
                    margin-left: 12px;
                }

                /* Quantity and Actions */
                .quantity-section {
                    display: flex;
                    align-items: center;
                    gap: 16px;
                    margin-bottom: 24px;
                }

                .quantity-controls {
                    display: flex;
                    align-items: center;
                    border: 1px solid #ddd;
                    border-radius: 4px;
                }

                .quantity-btn {
                    background: none;
                    border: none;
                    padding: 12px 16px;
                    cursor: pointer;
                    font-size: 18px;
                    color: #666;
                }

                .quantity-btn:hover {
                    background: #f5f5f5;
                }

                .quantity-input {
                    border: none;
                    padding: 12px;
                    text-align: center;
                    width: 60px;
                    font-size: 16px;
                }

                .wishlist-btn {
                    background: none;
                    border: 1px solid #ddd;
                    padding: 12px 20px;
                    border-radius: 4px;
                    cursor: pointer;
                    transition: all 0.3s;
                    display: flex;
                    align-items: center;
                    gap: 8px;
                }

                .wishlist-btn:hover {
                    border-color: #333;
                }

                .wishlist-btn.active {
                    background: #333;
                    color: white;
                    border-color: #333;
                }

                .add-to-cart {
                    background: black;
                    color: white;
                    border: none;
                    padding: 16px 24px;
                    border-radius: 4px;
                    font-size: 16px;
                    cursor: pointer;
                    width: 100%;
                    margin-bottom: 24px;
                    transition: background 0.3s;
                }

                .add-to-cart:hover {
                    background: #555;
                }

                /* Additional Info */
                .additional-info {
                    border-top: 1px solid #e0e0e0;
                    padding-top: 24px;
                    background-color:white;
                }

                .info-toggle {
                    background: none;
                    border: none;
                    width: 100%;
                    text-align: left;
                    font-size: 16px;
                    font-weight: 600;
                    padding: 16px 0;
                    cursor: pointer;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }

                .info-content {
                    padding: 16px 0;
                    color: #666;
                    line-height: 1.6;
                }

                /* Related Products */
                .related-products {
                    margin-top: 80px;
                }

                .section-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 32px;
                }

                .section-title {
                    font-size: 24px;
                    font-weight: 600;
                }

                .more-products-btn {
                    background: none;
                    border: none;
                    color: #333;
                    font-size: 16px;
                    cursor: pointer;
                    display: flex;
                    align-items: center;
                    gap: 8px;
                    text-decoration: underline;
                }

                .products-carousel {
                    position: relative;
                }

                .products-container {
                    display: flex;
                    overflow-x: auto;
                    gap: 20px;
                    scroll-behavior: smooth;
                    scrollbar-width: none;
                    -ms-overflow-style: none;
                }

                .products-container::-webkit-scrollbar {
                    display: none;
                }

                .product-card {
                    flex: 0 0 280px;
                    background: white;
                    border-radius: 8px;
                    overflow: hidden;
                    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
                    transition: transform 0.3s;
                    cursor: pointer;
                    position: relative;
                }

                .product-card:hover {
                    transform: translateY(-4px);
                }

                .product-card img {
                    width: 100%;
                    height: 200px;
                    object-fit: cover;
                }

                .card-badge {
                    position: absolute;
                    top: 12px;
                    left: 12px;
                    background: #28a745;
                    color: white;
                    padding: 4px 8px;
                    border-radius: 4px;
                    font-size: 11px;
                    font-weight: 600;
                }

                .card-discount {
                    position: absolute;
                    top: 12px;
                    right: 12px;
                    background: #dc3545;
                    color: white;
                    padding: 4px 8px;
                    border-radius: 4px;
                    font-size: 11px;
                    font-weight: 600;
                }

                .card-content {
                    padding: 16px;
                }

                .card-title {
                    font-size: 14px;
                    font-weight: 500;
                    margin-bottom: 8px;
                    line-height: 1.4;
                }

                .card-price {
                    font-weight: 600;
                }

                .card-original-price {
                    color: #999;
                    text-decoration: line-through;
                    font-size: 14px;
                    margin-left: 8px;
                }

                .scroll-controls {
                    position: absolute;
                    bottom: -50px;
                    left: 50%;
                    transform: translateX(-50%);
                    display: flex;
                    gap: 8px;
                }

                .scroll-btn {
                    background: white;
                    border: 1px solid #ddd;
                    border-radius: 50%;
                    width: 40px;
                    height: 40px;
                    cursor: pointer;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    transition: all 0.3s;
                }

                .scroll-btn:hover {
                    background: #f5f5f5;
                    border-color: #333;
                }


                /* Footer */
                .footer {
                    background-color: black;
                    padding: 3rem 0 1rem;
                    color: black;
                }

                .footer-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .footer-nav {
                    display: flex;
                    flex-direction: column;
                    background:black;
                    gap: 1rem;
                    margin-bottom: 2rem;
                    align-items: center;
                }

                .footer-nav .nav-link1 {
                    color: #ccc;
                    font-size: 0.9rem;
                    padding: 0.7rem 0;
                    width: 100%;
                    text-align: center;
                    background-color: black;
                    transition: color 0.3s, background-color 0.3s;
                    text-decoration: none;
                }

                .footer-nav .nav-link1:hover {
                    color: #fff;
                    background-color: black;
                    text-decoration: none;
                }

                .social-icons {
                    display: flex;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 2rem;
                }

                .social-icon {
                    width: 40px;
                    height: 40px;
                    background-color: #333;
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #fff;
                    text-decoration: none;
                    transition: background-color 0.3s;
                    font-size: 1.1rem;
                    background-color: black;
                }

                .social-icon:hover {
                    background-color: #555;
                }

                .footer-text {
                    color: #666;
                    font-size: 0.85rem;
                    text-align: center;
                }
                a {
                    text-decoration:none;
                }
                /* Responsive Design */
                @media (min-width: 768px) {
                    .mobile-menu-btn {
                        display: none;
                    }

                    .desktop-nav, .header-right {
                        display: flex;
                    }

                    .product-container {
                        grid-template-columns: 1fr;
                        gap: 32px;
                    }

                    .product-images {
                        flex-direction: column-reverse;
                    }

                    .thumbnail-images {
                        flex-direction: row;
                        justify-content: center;
                    }

                    .main-content {
                        padding: 20px;
                    }

                    .products-container {
                        gap: 16px;
                    }

                    .product-card {
                        flex: 0 0 250px;
                    }

                    .footer-nav {
                        flex-direction: row;
                        justify-content: center;
                        flex-wrap: wrap;
                        gap: 2.5rem;
                        background-color: black;
                    }

                    .footer-nav .nav-link1 {
                        width: auto;
                        border-bottom: none;
                        padding: 0.5rem 0;
                        background-color: black;
                        text-decoration: none;

                    }

                    .footer-nav .nav-link1:hover {
                        background-color: black;
                        color: #black;
                        text-decoration: none;
                    }

                }
                /* --- Main Container --- */
                .product-page-container {
                    width: 100%;
                    min-height: 100vh;
                    background-color: #ffffff; /* Ensure container background is white */
                }

                .main-content {
                    max-width: 1200px;
                    margin: 0 auto;
                    padding: 40px 20px;
                }

                /* --- Product Details Grid --- */
                .product-grid {
                    display: grid;
                    grid-template-columns: 1fr; /* Single column on mobile */
                    gap: 2rem;
                    align-items: flex-start;
                }

                @media (min-width: 768px) {
                    .product-grid {
                        grid-template-columns: 1fr 1fr; /* Two columns on desktop */
                        gap: 3rem;
                    }
                }

                /* --- Left Column: Images --- */
                .product-images-column {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 1rem;
                }

                .product-image-wrapper {
                    position: relative;
                    width: 100%;
                    border-radius: 8px;
                    overflow: hidden;
                }

                .product-image-wrapper img {
                    width: 100%;
                    height: auto;
                    display: block;
                    object-fit: cover;
                }

                .product-badge {
                    position: absolute;
                    top: 12px;
                    left: 12px;
                    background: #fff;
                    color: #000;
                    padding: 4px 10px;
                    border-radius: 4px;
                    font-size: 12px;
                    font-weight: 600;
                    border: 1px solid #eee;
                }



                /* --- Right Column: Product Info --- */
                .product-info-column h2 {
                    font-size: 2.5rem;
                    font-weight: 00;
                    margin-bottom: 16px;
                    line-height: 1.2;
                    color: #111827;
                }

                .product-description {
                    color: #4b5563;
                    margin-bottom: 24px;
                }

                .price-section {
                    margin-bottom: 32px;
                    display: flex;
                    align-items: baseline;
                    gap: 1rem;
                }

                .current-price {
                    font-size: 1.8rem;
                    font-weight: 500;
                    color: #111827;
                }

                .original-price {
                    font-size: 1.2rem;
                    color: #9ca3af;
                    text-decoration: line-through;
                }

                /* --- Actions: Quantity, Wishlist, Add to Cart --- */
                .actions-row {
                    display: flex;
                    flex-direction: column;
                    gap: 1rem;
                    margin-bottom: 1.5rem;
                }

                @media (min-width: 768px) {
                    .actions-row {
                        flex-direction: row;
                    }
                }

                .quantity-controls {
                    display: flex;
                    align-items: center;
                    border: 1px solid #d1d5db;
                    border-radius: 8px;
                    overflow: hidden;
                }

                .quantity-btn {
                    background: #fff;
                    border: none;
                    padding: 12px 16px;
                    cursor: pointer;
                    font-size: 1rem;
                    color: #4b5563;
                }

                .quantity-btn:hover {
                    background: #f9fafb;
                }

                .quantity-input {
                    border: none;
                    border-left: 1px solid #d1d5db;
                    border-right: 1px solid #d1d5db;
                    padding: 12px;
                    text-align: center;
                    width: 60px;
                    font-size: 1rem;
                    font-weight: 500;
                    -moz-appearance: textfield; /* Firefox */
                }
                .quantity-input::-webkit-outer-spin-button,
                .quantity-input::-webkit-inner-spin-button {
                    -webkit-appearance: none;
                    margin: 0;
                }

                .wishlist-btn {
                    background: #fff;
                    border: 1px solid #d1d5db;
                    padding: 12px 20px;
                    border-radius: 8px;
                    cursor: pointer;
                    transition: all 0.2s;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 8px;
                    font-weight: bold;
                    flex-grow: 1;
                }

                .wishlist-btn.active, .wishlist-btn:hover {
                    border-color: #111827;
                    background: #f9fafb;
                }

                .wishlist-btn .heart {
                    color: ${isWishlisted ? '#ef4444' : '#9ca3af'};
                    transition: color 0.2s;
                }

                .add-to-cart-btn {
                    background: #111827;
                    color: #fff;
                    border: none;
                    padding: 16px 24px;
                    border-radius: 8px;
                    font-size: 1rem;
                    font-weight: 500;
                    cursor: pointer;
                    width: 100%;
                    transition: background 0.2s;
                }

                .add-to-cart-btn:hover {
                    background: #374151;
                }

                /* --- Additional Info --- */
                .additional-info {
                    border-top: 1px solid #e5e7eb;
                    margin-top: 24px;
                }

                .info-toggle {
                    background: none;
                    border: none;
                    width: 100%;
                    text-align: left;
                    font-size: 1rem;
                    font-weight: 500;
                    padding: 16px 0;
                    cursor: pointer;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }

                .info-content {
                    padding-bottom: 16px;
                    color: #4b5563;
                }

                /* --- Related Products Section --- */
                .related-products {
                    margin-top: 80px;
                }

                .section-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 32px;
                }

                .section-title {
                    font-size: 1.75rem;
                    font-weight: 600;
                }

                .more-products-link {
                    color: #111827;
                    font-size: 1rem;
                    font-weight: 500;
                    text-decoration: none;
                    transition: color 0.2s;
                }

                .more-products-link:hover {
                    color: #4b5563;
                }

                .products-grid {
                    display: grid;
                    grid-template-columns: repeat(2, 1fr); /* 2 columns on mobile */
                    gap: 1.5rem;
                }

                @media (min-width: 1024px) {
                    .products-grid {
                        grid-template-columns: repeat(4, 1fr); /* 4 columns on desktop */
                    }
                }

                .related-product-card {
                    background: #fff;
                    border-radius: 8px;
                    overflow: hidden;
                    transition: transform 0.2s;
                    text-decoration: none;
                    color: inherit;
                    display: block;
                }

                .related-product-card:hover {
                    transform: translateY(-4px);
                }

                .related-product-card img {
                    width: 100%;
                    height: 250px;
                    object-fit: cover;
                    background-color: #f3f4f6;
                }

                .related-card-content {
                    padding: 12px 4px;
                }

                .related-card-title {
                    font-size: 1rem;
                    font-weight: 500;
                    margin-bottom: 4px;
                }

                .related-card-price {
                    font-weight: 500;
                    font-size: 1rem;
                }

                .related-card-original-price {
                    color: #9ca3af;
                    text-decoration: line-through;
                    font-size: 0.9rem;
                    margin-left: 8px;
                }



                }
            `}</style>

            <div className="container">
                {/* Discount Banner */}
                {isDiscountVisible && (
    <div className="discount-banner">
        <span className="discount-icon"><img src="ticket-percent2.png"/></span>
        10% off storeLimited time!<a
    href="/"
    target="_blank"
    rel="noopener"
    className="shop-now"
  >
    ShopNow <img src="arrow-right.png" />
  </a>
        <button 
            className="discount-close"
            onClick={() => setIsDiscountVisible(false)}
        >

        </button>
    </div>
)}

                {/* Header */}
                <header className="header">
                    <div className="header-content">
                        <button 
                            className="mobile-menu-btn"
                            onClick={() => setIsMobileMenuOpen(true)}
                        >
                            <MenuIcon />
                        </button>


                        <div className="header-right">
                            <div className="user-icon desktop-only">
                                <UserIcon />
                            </div>
                        </div>

                        <div className="header-icon">
                                <ShoppingBagIcon />
                            </div>
                    </div>
                </header>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <div className="mobile-menu-header">
                            <button 
                                className="close-btn"
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                <CloseIcon />
                            </button>
                            <div>
                            <NavLinks isMobile={true} onItemClick={scrollToSection} />
                            </div>
                            </div>
                    </div>
                )}  

                 {/* Main Content */}
            <main className="main-content">
                    {/* Product Section */}
                    <div className="product-layout">
                        {/* Product Images */}
                        <div className="product-images-section">
                            <div className="main-product-image">
                                <img src={product.images[0]} alt={product.name} />
                                <div className="product-badge">NEW</div>
                                <div className="discount-badge">-10%</div>
                            </div>
                            <div className="secondary-image">
                                <img src={product.images[1]} alt={`${product.name} additional view`} />
                            </div>
                        </div>

                        {/* Product Info */}
                        <div className="product-info-section">
                            <h1 className="product-title">{product.name}</h1>

                            <p className="product-description">{product.description}</p>

                            <div className="price-container">
                                <span className="current-price">${product.price.toFixed(2)}</span>
                                <span className="original-price">${product.originalPrice.toFixed(2)}</span>
                            </div>

                            <div className="product-controls">
                                <div className="quantity-wishlist-row">
                                    <div className="quantity-selector">
                                        <button 
                                            className="quantity-btn" 
                                            onClick={() => handleQuantityChange(-1)}
                                            aria-label="Decrease quantity"
                                        >

                                        </button>
                                        <input 
                                            type="number" 
                                            value={quantity} 
                                            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                                            className="quantity-display"
                                            min="1"
                                            aria-label="Product quantity"
                                        />
                                        <button 
                                            className="quantity-btn" 
                                            onClick={() => handleQuantityChange(1)}
                                            aria-label="Increase quantity"
                                        >
                                            +
                                        </button>
                                    </div>

                                    <button
                                        className={`wishlist-button ${isWishlisted ? 'active' : ''}`}
                                        onClick={() => setIsWishlisted(!isWishlisted)}
                                        aria-label={isWishlisted ? 'Remove from wishlist' : 'Add to wishlist'}
                                    >
                                        <img src="Heart.png" alt="Heart" style={{width: '16px'}} />
                                        Wishlist
                                    </button>
                                </div>

                                <button className="add-to-cart-button">
                                    Add to Cart
                                </button>
                            </div>

                            <div className="additional-info-section">
                                <button 
                                    className="info-toggle-button"
                                    onClick={() => setShowAdditionalInfo(!showAdditionalInfo)}
                                    aria-expanded={showAdditionalInfo}
                                >
                                    Additional Info
                                    <span className="info-toggle-icon">
                                        {showAdditionalInfo ? '' : '+'}
                                    </span>
                                </button>
                                {showAdditionalInfo && (
                                    <div className="info-content">
                                        <h4>Details</h4>
                                        <p>{product.details}</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Related Products */}
                    <section className="related-products-section">
                        <div className="section-header">
                            <h2 className="section-title">You might also like</h2>
                            <button className="more-products-link">More Products </button>
                        </div>

                        <div className="products-scroll-container">
                            <div className="products-grid" ref={scrollRef}>
                                {relatedProducts.map((item) => (
                                    <div key={item.id} className="product-card" onClick={() => navigateToProduct(item.id)}>
                                        <div className="product-card-image">
                                            <img src={item.image} alt={item.name} />
                                            {item.badge && <div className="card-badge">{item.badge}</div>}
                                            {item.discount && <div className="card-discount">{item.discount}</div>}
                                        </div>
                                        <div className="product-card-info">
                                            <h3 className="product-card-title">{item.name}</h3>
                                            <div className="product-card-price">
                                                <span className="card-current-price">${item.price.toFixed(2)}</span>
                                                {item.originalPrice && (
                                                    <span className="card-original-price">
                                                        ${item.originalPrice.toFixed(2)}
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>

                            <div className="scroll-navigation">
                                <button 
                                    className="scroll-nav-btn" 
                                    onClick={() => scrollProducts('left')}
                                    aria-label="Scroll left"
                                >

                                </button>
                                <button 
                                    className="scroll-nav-btn" 
                                    onClick={() => scrollProducts('right')}
                                    aria-label="Scroll right"
                                >

                                </button>
                            </div>
                        </div>
                    </section>
                </main>



                {/* Footer */}
                <footer className="footer">
                    <div className="footer-content">
                        <nav className="footer-nav">
                            <button onClick={() => scrollToSection(homeRef)} className="nav-link1">Home</button>
                            <button onClick={() => scrollToSection(shopRef)} className="nav-link1">Shop</button>
                            <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr"><button className="nav-link1">2K Community</button></a>
                            <button onClick={handleMediaKitDownload} className="nav-link1">Media kit</button>
                            <button onClick={() => console.log('Contact Us clicked')} className="nav-link1">Contact Us</button>
                        </nav>

                        <div className="social-icons">
                            <a href="" className="social-icon"><img src="instagram1.png"/></a>
                            <a href="" className="social-icon"><img src="facebook1.png"/></a>
                            <a href="" className="social-icon"><img src="youtube1.png"/></a>
                        </div>

                        <p className="footer-text"> 2025 BURNITDOWNYT. All rights reserved.</p>
                    </div>
                </footer>
            </div>
        </>
    );
};

export default Product;
