import React, { useEffect, useState } from 'react'
import Card from './Card'
const BIDapp = () => {
    const [search, setSearch] = useState("Search");
    const [newsData, setNewsData] = useState(null)
    const [loading, setLoading] = useState(true);
    const API_KEY = "9c3ed8ee95884dec979460a60f96675b";
    const mockNewsData = [
        {
            title: "165 New Stars Roster Reveal",
            urlToImage: "/2k25-1.jpg",
            url: "https://www.youtube.com/watch?v=EdYsk6HFPgY",
            duration: "4:45",
        },
        {
            title: "Current Male Roster Reveal",
            urlToImage: "/2k25-2.jpg",
            url: "https://www.youtube.com/watch?v=UNVNxNmYXn8",
            duration: "9:22",
        },
        {
            title: "Current Female Roster Reveal",
            urlToImage: "/2k25-3.jpg",
            url: "https://www.youtube.com/watch?v=HaNmPjuoCpQ&t=1s",
            duration: "5:38",
        },
        {
            title: "30 New Stars and 6 New Gimmicks",
            urlToImage: "/2k25-4.jpg",
            url: "https://www.youtube.com/watch?v=CBXgVaBFyeY",
            duration: "2:33",
        },
        {
            title: "Women's Money In The Bank Ladder Match",
            urlToImage: "/2k25-5.jpg",
            url: "https://www.youtube.com/watch?v=5RchjEwj5mM&t=1s",
            duration: "28:40",
        },
        {
            title: "John Cena vs Roman Reigns",
            urlToImage: "/2k25-6.jpg",
            url: "https://www.youtube.com/watch?v=IZJV-lUg9x4",
            duration: "16:24",
        }
    ];
    /*const getData = async() =>{
        const response = await fetch(`https://newsapi.org/v2/everything?q=${search}&apiKey=${API_KEY}`);
        const jsonData = await response.json();
        console.log(jsonData.articles);
        let dt = jsonData.articles.slice(0,10)
        setNewsData(dt)

    }*/
    useEffect(() => {
        // Simulate API call
        setTimeout(() => {
            setNewsData(mockNewsData);
            setLoading(false);
        }, 1000);
    }, []);

    /*useEffect(()=>{
        getData()
    },[])*/

    const handleInput = (e) =>{
        //console.log(e.target.value);
        setSearch(e.target.value)

    }
    const userInput = (event) =>{
        setSearch(event.target.value);
        // In real app, this would trigger a new API call
        console.log("Category selected:", event.target.value);
    }

    const getData = () => {
        setLoading(true);
        // Simulate search
        setTimeout(() => {
            setNewsData(mockNewsData.filter(item => 
                item.title.toLowerCase().includes(search.toLowerCase()) ||
                item.description.toLowerCase().includes(search.toLowerCase())
            ));
            setLoading(false);
        }, 500);
    };
const VideoCard = ({ data }) => (
        <div style={{
            background: '#2a2a2a',
            borderRadius: '8px',
            overflow: 'hidden',
            cursor: 'pointer',
            transition: 'transform 0.2s',
            minWidth: '280px',
            maxWidth: '320px'
        }}>
            <div style={{ position: 'relative' }}>
                <img 
                    src={data.urlToImage} 
                    alt={data.title}
                    style={{
                        width: '100%',
                        height: '180px',
                        objectFit: 'cover'
                    }}/>
                <div style={{
                    position: 'absolute',
                    bottom: '8px',
                    right: '8px',
                    background: 'rgba(0,0,0,0.8)',
                    color: 'white',
                    padding: '2px 6px',
                    borderRadius: '4px',
                    fontSize: '12px'
                }}>
                  {data.duration}
                </div>
            </div>
            <div style={{
                padding: '12px',
                color: 'white'
            }}>
                <h3 style={{
                    margin: '0 0 8px 0',
                    fontSize: '14px',
                    fontWeight: '600',
                    lineHeight: '1.3'
                }}>
                    {data.title}
                </h3>
                <p style={{
                    margin: '0',
                    fontSize: '12px',
                    color: '#ccc',
                    opacity: '0.8'
                }}>
                    Official
                </p>
            </div>
        </div>
    );
 return (
    <div style={{
            background: '#000',
            minHeight: '100vh',
            color: 'white',
            fontFamily: 'Arial, sans-serif'
        }}>
            {/* Header */}
            <header style={{
                background: '#1a1a1a',
                padding: '12px 20px',
                display: 'flex',
                alignItems: 'center',
                borderBottom: '1px solid #333'
            }}>
                <div style={{
                    display: 'flex',
                    alignItems: 'center'
                }}>
                    <div style={{
                        marginRight: '20px',
                        display: 'flex',
                        alignItems: 'center'
                    }}>
                        <img 
                            src="/RBID.jpg" 
                            alt="RBID"
                            style={{
                                height: '40px',
                                width: 'auto',
                                borderRadius: '4px'
                            }}
                        />
                    </div>
                    <h1 style={{
                        margin: '0',
                        fontSize: '24px',
                        fontWeight: 'bold',
                        color: '#D32F2F'
                    }}>
                         BURNITDOWN
                    </h1>
                </div>
        <nav style={{
                    display: 'flex',
                    gap: '20px',
                    alignItems: 'center',
                    marginLeft: '40px',
                }}>
                    {['NEWS', 'TICKETS', 'MEDIAKIT', 'SHOWS', 'SHOP'].map((item, index) => (
                        <button
                            key={item}
                            style={{
                                background: index === 0 ?'#1a1a1a' : 'transparent',
                                color: 'white',
                                border: 'none',
                                padding: '10px 20px',
                                cursor: 'pointer',
                                fontSize: '14px',
                                fontWeight: '600',
                                borderRadius: '4px'
                            }}
                        >
                            {item}
                        </button>
                    ))}
                </nav>

                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '10px'
                }}>
                    <input
                        type="text"
                        placeholder="Search"
                        value={search}
                        onChange={handleInput}
                        style={{
                            padding: '8px 12px',
                            background: '#2a2a2a',
                            border: '1px solid #444',
                            borderRadius: '4px',
                            color: 'white',
                            fontSize: '14px'
                        }}
                    />
                    <button
                        onClick={getData}
                        style={{
                            padding: '8px 16px',
                            background: '#c41e3a',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            fontSize: '14px',
                            fontWeight: '600',
                        }}
                    >
                        Search
                    </button>
                </div>
            </header>

            {/* Latest Videos Section */}
            <section style={{ 
                padding: '20px 0',
                maxWidth: '1800px',
                margin: '0 auto'
            }}>
                <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '20px',
                    padding: '0 20px'
                }}>
                    <h2 style={{
                        margin: '0',
                        fontSize: '24px',
                        fontWeight: 'bold'
                    }}>
                        Latest Videos
                    </h2>
                    <button style={{
                        background: 'transparent',
                        color: '#c41e3a',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '14px',
                        fontWeight: '600'
                    }}>
                        View All
                    </button>
                </div>
              {/* Video Grid with Auto-scroll */}
                {loading ? (
                    <div style={{
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        height: '200px',
                        color: '#666'
                    }}>
                        Loading...
                    </div>
                ) : (
                    <div style={{
                        overflow: 'hidden',
                        position: 'relative',
                        margin: '0 20px'
                    }}>
                        <div style={{
                            display: 'flex',
                            gap: '20px',
                            animation: 'scroll 20s linear infinite',
                            width: 'fit-content'
                        }}>
                            {[...newsData, ...newsData].map((item, index) => (
                                <VideoCard key={index} data={item} />
                            ))}
                            {[...newsData, ...newsData].map((item,index) =>(
                                <a key={index} href={item.url} target="_blank"
                                rel="noopener noreferrer" style={{textDecoration: 'none', display: 'inline-block',
                                    marginRight: '20px'
                                }}>
                                    <VideoCard data={item}/>
                                </a>
                            ))}
                        </div>
                        <style>
                            {`
                                @keyframes scroll {
                                    0% {
                                        transform: translateX(0);
                                    }
                                    100% {
                                        transform: translateX(-50%);
                                    }
                                }
                            `}
                        </style>
                    </div>
                )}
            </section>


            {/* Additional Content Sections */}
            <section style={{
                padding: '20px',
                display: 'grid',
                gridTemplateColumns: '1fr 300px',
                gap: '20px',
                maxWidth: '1800px',
                margin: '0 auto'
            }}>
                {/* Left Column - Main Content */}
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '20px'
                }}>
                    {/* Merch Shop Card */}
                    <div style={{
                        background: '#c41e3a',
                        padding: '30px',
                        borderRadius: '8px'
                    }}>
                        <p style={{
                            margin: '0 0 10px 0',
                            fontSize: '14px',
                            fontWeight: '600',
                            opacity: '0.9'
                        }}>
                            MERCH SHOP
                        </p>
                        <h3 style={{
                            margin: '0 0 15px 0',
                            fontSize: '28px',
                            fontWeight: 'bold'
                        }}>
                            Gear Up Like a Champion
                        </h3>
                        <p style={{
                            margin: '0 0 20px 0',
                            fontSize: '16px',
                            opacity: '0.9'
                        }}>
                            Browse authentic WWE merch, apparel, and fan gear built for true super-fans.
                        </p>
                        <button style={{
                            background: 'white',
                            color: '#c41e3a',
                            border: 'none',
                            padding: '12px 24px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            fontSize: '16px',
                            fontWeight: '600'
                        }}>
                            Shop Now
                        </button>
                    </div>

                    {/* Content Categories Grid */}
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(3, 1fr)',
                        gap: '20px'
                    }}>
                        <div style={{
                            background: '#1a1a1a',
                            borderRadius: '8px',
                            overflow: 'hidden',
                            border: '1px solid #333'
                        }}>
                            <div style={{
                                height: '150px',
                                background: '#333',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: '#666'
                            }}>
                                Championship Image
                            </div>
                            <div style={{ padding: '15px' }}>
                                <h4 style={{
                                    margin: '0 0 8px 0',
                                    fontSize: '16px',
                                    fontWeight: '600'
                                }}>
                                    Championship Highlights
                                </h4>
                                <p style={{
                                    margin: '0',
                                    fontSize: '14px',
                                    color: '#ccc'
                                }}>
                                    Latest championship match highlights and updates
                                </p>
                            </div>
                        </div>

                        <div style={{
                            background: '#1a1a1a',
                            borderRadius: '8px',
                            overflow: 'hidden',
                            border: '1px solid #333'
                        }}>
                            <div style={{
                                height: '150px',
                                background: '#333',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: '#666'
                            }}>
                                Money in the Bank Image
                            </div>
                            <div style={{ padding: '15px' }}>
                                <h4 style={{
                                    margin: '0 0 8px 0',
                                    fontSize: '16px',
                                    fontWeight: '600'
                                }}>
                                    Money in the Bank
                                </h4>
                                <p style={{
                                    margin: '0',
                                    fontSize: '14px',
                                    color: '#ccc'
                                }}>
                                    Catch up on all the ladder match action
                                </p>
                            </div>
                        </div>

                        <div style={{
                            background: '#1a1a1a',
                            borderRadius: '8px',
                            overflow: 'hidden',
                            border: '1px solid #333'
                        }}>
                            <div style={{
                                height: '150px',
                                background: '#333',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: '#666'
                            }}>
                                WrestleMania Image
                            </div>
                            <div style={{ padding: '15px' }}>
                                <h4 style={{
                                    margin: '0 0 8px 0',
                                    fontSize: '16px',
                                    fontWeight: '600'
                                }}>
                                    WrestleMania
                                </h4>
                                <p style={{
                                    margin: '0',
                                    fontSize: '14px',
                                    color: '#ccc'
                                }}>
                                    Relive the grandest stage of them all
                                </p>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Right Sidebar */}
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '20px'
                }}>
                    {/* Event Preview */}
                    <div style={{
                        background: '#c41e3a',
                        padding: '20px',
                        borderRadius: '8px'
                    }}>
                        <p style={{
                            margin: '0 0 10px 0',
                            fontSize: '12px',
                            fontWeight: '600',
                            opacity: '0.9'
                        }}>
                            EVENT PREVIEW FOR 7/6
                        </p>
                        <h3 style={{
                            margin: '0',
                            fontSize: '18px',
                            fontWeight: 'bold',
                            lineHeight: '1.2'
                        }}>
                            What will be the fallout from the latest championship match?
                        </h3>
                    </div>

                    {/* Latest News */}
                    <div style={{
                        background: '#1a1a1a',
                        padding: '20px',
                        borderRadius: '8px',
                        border: '1px solid #333'
                    }}>
                        <h3 style={{
                            margin: '0 0 15px 0',
                            fontSize: '16px',
                            fontWeight: '600'
                        }}>
                            LATEST NEWS
                        </h3>
                        <div style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '10px'
                        }}>
                            <div style={{
                                background: '#2a2a2a',
                                padding: '12px',
