import React, { useState } from 'react';
import {Link} from "react-router-dom"
const Tickets = () => {
    const [search, setSearch] = useState("");
    const [loading, setLoading] = useState(true);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // Navigation items matching the news.js structure
    const navItems = ['NEWS', 'HOME', 'MEDIAKIT', 'SHOWS', 'SHOP'];

    // Event data for the tickets page
    const featuredEvent = {
        title: "Championship Night: Final Showdown",
        description: "Don't miss the most anticipated wrestling event of the year. Secure your seats now for an unforgettable night of championship action.",
        date: "August 15, 2025",
        venue: "Miami in FL",
        status: "HOT TICKET",
        availability: "Limited Availability"
    };
    const handleSearch = () => {
        // Search functionality is handled by the filter above
        setLoading(true);
        setTimeout(() => setLoading(false), 500);
    };
    const events = [
        {
            id: 1,
            title: "SummerSlam Championship",
            description: "The biggest wrestling event of the summer featuring championship matches and surprise appearances.",
            image: "Crown.png",
            date: "August 15, 2025 at 8:00 PM",
            venue: "Hard Rock Stadium, Miami FL",
            price: "$89",
            status: "ON SALE"
        },
        {
            id: 2,
            title: "Monday Night Live",
            description: "Weekly live wrestling entertainment with your favorite superstars and emerging talent.",
            image: "AJ.png",
            date: "Every Monday at 8:00 PM",
            venue: "WWE Network & Arenas Close",
            price: "$45",
            status: "ON SALE"
        },
        {
            id: 3,
            title: "Championship Clash",
            description: "Top-quality championship matches in the heart of Las Vegas.",
            image: "serpent.png",
            date: "September 22, 2025 at 7:30 PM",
            venue: "T-Mobile Arena Las Vegas, NV",
            price: "$125",
            status: "ON SALE"
        }
    ];

    // Seating chart data
    const seatingOptions = [
        { type: "VIP", price: "$200", color: "#FFD700" },
        { type: "Premium", price: "$100-$150", color: "#C0C0C0" },
        { type: "General", price: "$45-$89", color: "#CD7F32" }
    ];

    // Navigation component that handles both desktop and mobile
    const NavLinks = ({ isMobile = false }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
            {navItems.map((item) => {
                if (item === 'MEDIAKIT') {
                    return (
                        <a
                            key={item}
                            href="Media-kit.pdf"
                            download="mediakit.pdf"
                            className="nav-link"
                            onClick={() => isMobile && setIsMobileMenuOpen(false)}
                        >
                            {item}
                        </a>
                    );
                }
                else if (item === "NEWS") {
                                    return (
                                        <Link
                                        key={item}
                                        className="nav-link"
                                        to="/news"
                                        onClick={() => isMobile && setIsMobileMenuOpen(false)}>{item}</Link>
                                    )
                                }
                else if (item === "HOME") {
                                    return (
                                        <Link
                                        key={item}
                                        className="nav-link"
                                        to="/"
                                        onClick={() => isMobile && setIsMobileMenuOpen(false)}>{item}</Link>
                                    )
                                }
                else if (item === "SHOWS") {
                                    return (
                                        <Link
                                        key={item}
                                        className="nav-link"
                                        to="/shows"
                                        onClick={() => isMobile && setIsMobileMenuOpen(false)}>{item}</Link>
                                    )
                                }
                return (
                    <button
                        key={item}
                        className="nav-link"
                        onClick={() => {
                            console.log(item, 'clicked');
                            if (isMobile) setIsMobileMenuOpen(false);
                        }}
                    >
                        {item}
                    </button>
                );
            })}
        </nav>
    );

    return (
        <>
            {/* Comprehensive CSS styles for the tickets page */}
            <style>{`
                :root {
                    --main-red: #C0C0C0;
                    --dark-bg: #000;
                    --primary-bg: #111;
                    --secondary-bg: #1a1a1a;
                    --tertiary-bg: #2a2a2a;
                    --border-color: #333;
                    --text-light: #fff;
                    --text-dark: #ccc;
                    --text-muted: #666;
                    --gradient-purple: linear-gradient(135deg, #C0C0C0 0%, #341C5C 100%);
                    --accent-blue: #4A90E2;
                    --accent-gold: #FFD700;
                    --accent-green: #4CAF50;
                }

                * {
                    box-sizing: border-box;
                }

                body {
                    margin: 0;
                    background-color: var(--dark-bg);
                    color: var(--text-light);
                    font-family: 'Roboto', 'Arial', sans-serif;
                    line-height: 1.6;
                }

                .app-container {
                    background-color: var(--dark-bg);
                    min-height: 100vh;
                }

                /* Header Styles */
                .app-header {
                    background-color: var(--secondary-bg);
                    padding: 1rem 2rem;
                    border-bottom: 1px solid var(--border-color);
                    position: sticky;
                    top: 0;
                    z-index: 100;
                }

                .header-content {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    max-width: 1800px;
                    margin: 0 auto;
                }
                
                .logo-container {
                    display: flex;
                    align-items: center;
                    gap: 1rem;
                }

                .logo-img {
                    height: 40px;
                    border-radius: 4px;
                }

                .logo-title {
                    margin: 0;
                    font-size: 1.75rem;
                    font-weight: bold;
                    color: var(--main-red);
                    text-transform: uppercase;
                    font-family: 'League Gothic', Impact, sans-serif;
                }

                .desktop-nav {
                    display: none;
                }

                .header-search-container {
                    display: none;
                }

                .hamburger-btn {
                    background: none;
                    border: none;
                    color: var(--text-light);
                    font-size: 2rem;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: absolute;
                    top: 74px;
                    left: 0;
                    right: 0;
                    background: var(--secondary-bg);
                    border-bottom: 1px solid var(--border-color);
                    z-index: 1000;
                    padding: 1.5rem;
                    display: flex;
                    flex-direction: column;
                    gap: 1rem;
                }
                
                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 0.5rem;
                }
                
                .mobile-search-container {
                    display: flex;
                    gap: 0.5rem;
                    margin-top: 1rem;
                }

                /* Navigation Links */
                .nav-link {
                    color: var(--text-light);
                    padding: 0.75rem 1rem;
                    font-size: 1rem;
                    font-weight: 600;
                    border-radius: 4px;
                    cursor: pointer;
                    background: transparent;
                    border: none;
                    text-decoration: none;
                    text-align: left;
                    transition: all 0.2s ease;
                }

                .nav-link:hover {
                    background-color: var(--tertiary-bg);
                    transform: translateY(-1px);
                }

                /* Search Elements */
                .search-input {
                    padding: 0.75rem 1rem;
                    background: var(--tertiary-bg);
                    border: 1px solid #444;
                    border-radius: 4px;
                    color: var(--text-light);
                    font-size: 1rem;
                    flex-grow: 1;
                    transition: border-color 0.2s ease;
                }

                .search-input:focus {
                    outline: none;
                    border-color: var(--main-red);
                }

                .search-btn {
                    padding: 0.75rem 1.5rem;
                    background: var(--main-red);
                    color: var(--text-light);
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    font-size: 1rem;
                    font-weight: 600;
                    transition: all 0.2s ease;
                }

                .search-btn:hover {
                    background-color: #FFFFF0;
                    transform: translateY(-1px);
                }


                /* Hero Section */
                .tickets-hero {
                    background: linear-gradient(rgba(255, 240, 255, 0.5), rgba(255, 240, 255, 0.5)), url('F.png') center/cover no-repeat;
                    padding: 4rem 2rem;
                    text-align: center;
                    margin-bottom: 2rem;
                }

                .tickets-hero h1 {
                    font-size: 3rem;
                    font-weight: bold;
                    margin: 0 0 1rem 0;
                    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
                }

                .tickets-hero p {
                    font-size: 1.2rem;
                    opacity: 0.9;
                    margin: 0 0 2rem 0;
                    max-width: 600px;
                    margin-left: auto;
                    margin-right: auto;
                }

                .browse-tickets-btn {
                    background: var(--accent-blue);
                    color: white;
                    border: none;
                    padding: 1rem 2rem;
                    border-radius: 25px;
                    font-size: 1rem;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                }

                .browse-tickets-btn:hover {
                    background: #357ABD;
                    transform: translateY(-2px);
                    box-shadow: 0 5px 15px rgba(74, 144, 226, 0.3);
                }

                /* Main Content */
                .main-content {
                    padding: 0 2rem 2rem;
                    max-width: 1800px;
                    margin: 0 auto;
                }

                /* Featured Event Section */
                .featured-event {
                    background: var(--secondary-bg);
                    border: 2px solid var(--main-red);
                    border-radius: 12px;
                    padding: 2rem;
                    margin-bottom: 3rem;
                    position: relative;
                    overflow: hidden;
                }

                .featured-event::before {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    height: 4px;
                    background: var(--gradient-purple);
                }

                .hot-ticket-badge {
                    display: inline-block;
                    background: #ff4444;
                    color: white;
                    padding: 0.5rem 1rem;
                    border-radius: 15px;
                    font-size: 0.8rem;
                    font-weight: bold;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                    margin-bottom: 1rem;
                }

                .availability-badge {
                    background: #ff8800;
                    color: white;
                    padding: 0.25rem 0.75rem;
                    border-radius: 10px;
                    font-size: 0.75rem;
                    font-weight: 600;
                    margin-left: 1rem;
                }

                .featured-title {
                    font-size: 2.5rem;
                    font-weight: bold;
                    margin: 0 0 1rem 0;
                    color: var(--text-light);
                }

                .featured-description {
                    font-size: 1.1rem;
                    color: var(--text-dark);
                    margin-bottom: 1.5rem;
                    line-height: 1.6;
                }

                .event-details {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 2rem;
                    margin-bottom: 2rem;
                    font-size: 1rem;
                }

                .event-detail {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    color: var(--accent-blue);
                    font-weight: 600;
                }

                /* Events Grid */
                .events-section h2 {
                    font-size: 2rem;
                    font-weight: bold;
                    margin: 0 0 2rem 0;
                    color: var(--text-light);
                }

                .events-grid {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;
                    margin-bottom: 3rem;
                }

                .event-card {
                    background: var(--secondary-bg);
                    border-radius: 12px;
                    overflow: hidden;
                    border: 1px solid var(--border-color);
                    transition: all 0.3s ease;
                    cursor: pointer;
                }

                .event-card:hover {
                    transform: translateY(-5px);
                    box-shadow: 0 10px 30px #FFFFF0;
                }

                .event-image-container {
                    position: relative;
                    height: 200px;
                    overflow: hidden;
                }

                .event-image {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    transition: transform 0.3s ease;
                }

                .event-card:hover .event-image {
                    transform: scale(1.05);
                }

                .event-status-badge {
                    position: absolute;
                    top: 1rem;
                    left: 1rem;
                    background: var(--accent-green);
                    color: white;
                    padding: 0.5rem 1rem;
                    border-radius: 15px;
                    font-size: 0.8rem;
                    font-weight: bold;
                    text-transform: uppercase;
                }

                .event-content {
                    padding: 1.5rem;
                }

                .event-title {
                    font-size: 1.5rem;
                    font-weight: bold;
                    margin: 0 0 1rem 0;
                    color: var(--text-light);
                }

                .event-description {
                    color: var(--text-dark);
                    margin-bottom: 1.5rem;
                    line-height: 1.5;
                }

                .event-info {
                    display: flex;
                    flex-direction: column;
                    gap: 0.5rem;
                    margin-bottom: 1.5rem;
                }

                .event-info-item {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    font-size: 0.9rem;
                    color: var(--text-dark);
                }

                .event-price {
                    font-size: 1.5rem;
                    font-weight: bold;
                    color: var(--accent-gold);
                    margin-bottom: 1rem;
                }

                .buy-tickets-btn {
                    width: 100%;
                    padding: 1rem;
                    background: var(--main-red);
                    color: white;
                    border: none;
                    border-radius: 8px;
                    font-size: 1rem;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                }

                .buy-tickets-btn:hover {
                    background: #C0C0C0;
                    transform: translateY(-2px);
                }

                /* Ticket Information Section */
                .ticket-info-section {
                    margin-bottom: 3rem;
                }

                .ticket-info-section h2 {
                    font-size: 2rem;
                    font-weight: bold;
                    margin: 0 0 2rem 0;
                    color: var(--text-light);
                }

                .ticket-info-grid {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;
                }

                .info-card {
                    background: var(--secondary-bg);
                    padding: 2rem;
                    border-radius: 12px;
                    text-align: center;
                    border: 1px solid var(--border-color);
                    transition: transform 0.3s ease;
                }

                .info-card:hover {
                    transform: translateY(-3px);
                }

                .info-icon {
                    font-size: 3rem;
                    margin-bottom: 1rem;
                }

                .info-title {
                    font-size: 1.25rem;
                    font-weight: bold;
                    margin: 0 0 1rem 0;
                    color: var(--text-light);
                }

                .info-description {
                    color: var(--text-dark);
                    line-height: 1.5;
                }

                /* Seating Chart Section */
                .seating-chart-section {
                    background: var(--secondary-bg);
                    padding: 2rem;
                    border-radius: 12px;
                    margin-bottom: 3rem;
                }

                .seating-chart-section h2 {
                    font-size: 2rem;
                    font-weight: bold;
                    margin: 0 0 2rem 0;
                    color: var(--text-light);
                    text-align: center;
                }

                .seating-options {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 1rem;
                    margin-top: 2rem;
                }

                .seating-option {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    padding: 1rem;
                    background: var(--tertiary-bg);
                    border-radius: 8px;
                    border-left: 4px solid;
                }

                .seating-option.vip {
                    border-left-color: var(--accent-gold);
                }

                .seating-option.premium {
                    border-left-color: #C0C0C0;
                }

                .seating-option.general {
                    border-left-color: #CD7F32;
                }

                .seating-type {
                    font-weight: bold;
                    font-size: 1.1rem;
                }

                .seating-price {
                    font-weight: bold;
                    font-size: 1.2rem;
                    color: var(--accent-gold);
                }

                .ring-placeholder {
                    width: 120px;
                    height: 80px;
                    background: var(--main-red);
                    border-radius: 8px;
                    margin: 2rem auto;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    font-weight: bold;
                    color: white;
                }

                /* Footer Styles */
                .app-footer {
                    background: var(--primary-bg);
                    padding: 3rem 2rem;
                    border-top: 1px solid var(--border-color);
                    margin-top: 3rem;
                }

                .footer-content {
                    max-width: 1800px;
                    margin: 0 auto;
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;
                }

                .footer-brand h3 {
                    margin: 0 0 1rem 0;
                    font-size: 1.5rem;
                    color: var(--main-red);
                    font-family: 'League Gothic', Impact, sans-serif;
                }

                .footer-brand p {
                    margin: 0 0 1.5rem 0;
                    color: var(--text-dark);
                    line-height: 1.5;
                }

                .social-links {
                    display: flex;
                    gap: 1rem;
                    flex-wrap: wrap;
                }

                .social-link {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    padding: 0.75rem 1rem;
                    border-radius: 8px;
                    text-decoration: none;
                    color: var(--text-light);
                    font-weight: 600;
                    transition: all 0.2s ease;
                }

                .social-link.facebook { background: #1877f2; }
                .social-link.youtube { background: #ff0000; }
                .social-link.instagram { background: #e4405f; }

                .social-link:hover {
                    transform: translateY(-2px);
                    box-shadow: 0 5px 15px rgba(0,0,0,0.3);
                }

                .social-icon {
                    width: 20px;
                    height: 20px;
                    filter: brightness(0) invert(1);
                }

                .footer-copyright {
                    border-top: 1px solid var(--border-color);
                    margin-top: 2rem;
                    padding-top: 2rem;
                    text-align: center;
                    color: var(--text-muted);
                    font-size: 0.9rem;
                }

                /* Responsive Design */
                @media (min-width: 768px) {
                    .events-grid {
                        grid-template-columns: repeat(2, 1fr);
                    }
                    
                    .ticket-info-grid {
                        grid-template-columns: repeat(3, 1fr);
                    }
                    
                    .seating-options {
                        grid-template-columns: repeat(3, 1fr);
                    }
                    
                    .footer-content {
                        grid-template-columns: 2fr 1fr;
                        align-items: start;
                    }
                }

                @media (min-width: 1024px) {
                    .hamburger-btn {
                        display: none;
                    }
                    
                    .desktop-nav {
                        display: flex;
                        align-items: center;
                        gap: 1rem;
                        margin-left: 2rem;
                    }
                    
                    .events-grid {
                        grid-template-columns: repeat(3, 1fr);
                    }
                    
                    .tickets-hero h1 {
                        font-size: 4rem;
                    }
                }

               @media (min-width: 1024px) {
                    .hamburger-btn {
                        display: none;
                    }
                    
                    .desktop-nav {
                        display: flex;
                        align-items: center;
                        gap: 1rem;
                        margin-left: 2rem;
                    }
                    
                    .header-search-container {
                        display: flex;
                        align-items: center;
                        gap: 0.5rem;
                        margin-left: auto;
                    }
                    
                    .news-grid {
                        grid-template-columns: repeat(3, 1fr);
                    }
                    
                    .news-hero h1 {
                        font-size: 4rem;
                    }
                }

                @media (min-width: 1400px) {
                    .news-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }
                }
            `}</style>

            <div className="app-container">
                {/* Header Section - Same as news.js */}
                <header className="app-header">
                    <div className="header-content">
                        <div className="logo-container">
                            <img src="BBID.jpg" alt="Burn It Down Logo" className="logo-img" />
                            <h1 className="logo-title">BURNITDOWNYT</h1>
                        </div>

                        {/* Desktop Navigation */}
                        <NavLinks />
                        {/* Desktop Search */}
                        <div className="header-search-container">
                            <input
                                type="text"
                                placeholder="Search News..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                                className="search-input"
                            />
                            <button onClick={handleSearch} className="search-btn">Search</button>
                        </div>
                        {/* Mobile Menu Button */}
                        <button className="hamburger-btn" onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
                            &#9776;
                        </button>
                    </div>
                </header>
                

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <NavLinks isMobile={true} />
                        <div className="mobile-search-container">
                            <input
                                type="text"
                                placeholder="Search News..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                className="search-input"
                            />
                            <button onClick={handleSearch} className="search-btn">Search</button>
                        </div>
                    </div>
                )}


                {/* Hero Section */}
                <section className="tickets-hero">
                    <h1>Event Tickets</h1>
                    <p>Get your tickets to the most exciting wrestling events</p>
                    <button className="browse-tickets-btn">
                        Browse Tickets
                    </button>
                </section>

                <main className="main-content">
                    {/* Featured Event Section */}
                    <div className="featured-event">
                        <div className="hot-ticket-badge">{featuredEvent.status}</div>
                        <span className="availability-badge">{featuredEvent.availability}</span>
                        
                        <h2 className="featured-title">{featuredEvent.title}</h2>
                        <p className="featured-description">{featuredEvent.description}</p>
                        
                        <div className="event-details">
                            <div className="event-detail">
                                📅 {featuredEvent.date}
                            </div>
                            <div className="event-detail">
                                📍 {featuredEvent.venue}
                            </div>
                        </div>
                    </div>

                    {/* Events Grid */}
                    <div className="events-section">
                        <div className="events-grid">
                            {events.map(event => (
                                <div key={event.id} className="event-card">
                                    <div className="event-image-container">
                                        <img src={event.image} alt={event.title} className="event-image" />
                                        <div className="event-status-badge">{event.status}</div>
                                    </div>
                                    <div className="event-content">
                                        <h3 className="event-title">{event.title}</h3>
                                        <p className="event-description">{event.description}</p>
                                        
                                        <div className="event-info">
                                            <div className="event-info-item">
                                                📅 {event.date}
                                            </div>
                                            <div className="event-info-item">
                                                📍 {event.venue}
                                            </div>
                                        </div>
                                        
                                        <div className="event-price">From {event.price}</div>
                                        <button className="buy-tickets-btn">Buy Tickets</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Ticket Information Section */}
                    <div className="ticket-info-section">
                        <h2>Ticket Information</h2>
                        <div className="ticket-info-grid">
                            <div className="info-card">
                                <div className="info-icon">⚖️</div>
                                <h3 className="info-title">Secure Booking</h3>
                                <p className="info-description">Safe and secure ticket purchasing with trusted payment processing</p>
                            </div>
                            <div className="info-card">
                                <div className="info-icon">👥</div>
                                <h3 className="info-title">Group Discounts</h3>
                                <p className="info-description">Special pricing for groups of 10 or more people</p>
                            </div>
                            <div className="info-card">
                                <div className="info-icon">💲</div>
                                <h3 className="info-title">Best Prices</h3>
                                <p className="info-description">Competitive pricing with no hidden fees</p>
                            </div>
                        </div>
                    </div>

                    {/* Seating Chart Section */}
                    <div className="seating-chart-section">
                        <h2>Seating Chart</h2>
                        <div className="ring-placeholder">RING</div>
                        <div className="seating-options">
                            {seatingOptions.map(option => (
                                <div key={option.type} className={`seating-option ${option.type.toLowerCase()}`}>
                                    <span className="seating-type">{option.type}</span>
                                    <span className="seating-price">{option.price}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                </main>

                {/* Footer - Same structure as news.js with social links */}
                <footer className="app-footer">
                    <div className="footer-content">
                        <div className="footer-brand">
                            <h3>BURNITDOWNYT</h3>
                            <p>Your ultimate destination for wrestling entertainment, news, and exclusive content. Stay connected with all the latest updates from the wrestling world.</p>
                            <div className="social-links">
                                <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr" className="social-link facebook" target="_blank" rel="noopener noreferrer">
                                    <img src="facebook.png" alt="Facebook" className="social-icon" />
                                    Facebook
                                </a>
                                <a href="https://youtube.com/@burnitdownyt?si=ogbNfYLps54zRZQ1" className="social-link youtube" target="_blank" rel="noopener noreferrer">
                                    <img src="youtube.png" alt="YouTube" className="social-icon" />
                                    YouTube
                                </a>
                                <a href="https://www.instagram.com/burnitdownyt?igsh=MTExOGNwOHJhZWYyYQ%3D%3D&utm_source=qr" className="social-link instagram" target="_blank" rel="noopener noreferrer">
                                    <img src="instagram.png" alt="Instagram" className="social-icon" />
                                    Instagram
                                </a>
                            </div>
                        </div>
                    </div>
                    <div className="footer-copyright">
                        &copy; 2025 Burn It Down. All rights reserved.
                    </div>
                </footer>
            </div>
        </>
    );
};

export default Tickets;
