import React, { useEffect, useState } from 'react'
import Card from './Card'

const BIDapp = () => {
    const [search, setSearch] = useState("Search");
    const [newsData, setNewsData] = useState(null)
    const API_KEY = "9c3ed8ee95884dec979460a60f96675b";

    const getData = async() =>{
        const response = await fetch(`https://newsapi.org/v2/everything?q=${search}&apiKey=${API_KEY}`);
        const jsonData = await response.json();
        console.log(jsonData.articles);
        let dt = jsonData.articles.slice(0,10)
        setNewsData(dt)

    }

    useEffect(()=>{
        getData()
    },[])

    const handleInput = (e) =>{
        console.log(e.target.value);
        setSearch(e.target.value)

    }
    const userInput = (event) =>{
        setSearch(event.target.value)
    }

  return (
    <div>
        <nav claasName="nav">
            <div>
                <h1><strong>BURN IT DOWN</strong></h1>
            </div>
            <ul style={{display:"flex", gap:"11px"}}>
                <a style={{fontWeight:600, fontSize:"17px", textAlign:"left",color:"white"}}><button>NEWS</button></a>
                <a style={{fontWeight:600, fontSize:"17px",color:"white"}}><button>TICKETS</button></a>
                <a href="/Media-kit.pdf" target="_blank" rel="noopener noreferrer" download style={{fontWeight:600, fontSize:"17px",color:"white"}}><button>MEDIAKIT</button></a>
                <a style={{fontWeight:600, fontSize:"17px",color:"white"}}><button>SHOWS</button></a>
                <a style={{fontWeight:600, fontSize:"17px",color:"white"}}><button>SHOP</button></a>
            </ul>
            <div className='searchBar'>
                <input type='text' placeholder='Search News' value={search} onChange={handleInput}/>
                <button onClick={getData}>Search</button>
            </div>
        </nav>
        <div>
            <p className='head'>Youtube Videos</p>
        </div>
        <div className='categoryBtn'>
            <button onClick={userInput} value="sports">Sports</button>
            <button onClick={userInput} value="politics">Politics</button>
            <button onClick={userInput} value="entertainment">Entertainment</button>
            <button onClick={userInput} value="health">Health</button>
            <button onClick={userInput} value="fitness">Fitness</button>
        </div>

        <div>
        {newsData?  <Card data={newsData}/> : null}

        </div>
    </div>
  )
}

export default BIDapp
