import React, { useEffect, useState } from 'react'

const BIDapp = () => {
    const [search, setSearch] = useState("Search");
    const [newsData, setNewsData] = useState(null)
    const [loading, setLoading] = useState(true);
    const [isMobile, setIsMobile] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const mockNewsData = [
        {
            title: "165 New Stars Roster Reveal",
            urlToImage: "/2k25-1.jpg",
            url: "https://www.youtube.com/watch?v=EdYsk6HFPgY",
            duration: "4:45",
        },
        {
            title: "Current Male Roster Reveal",
            urlToImage: "/2k25-2.jpg",
            url: "https://www.youtube.com/watch?v=UNVNxNmYXn8",
            duration: "9:22",
        },
        {
            title: "Current Female Roster Reveal",
            urlToImage: "/2k25-3.jpg",
            url: "https://www.youtube.com/watch?v=HaNmPjuoCpQ&t=1s",
            duration: "5:38",
        },
        {
            title: "30 New Stars and 6 New Gimmicks",
            urlToImage: "/2k25-4.jpg",
            url: "https://www.youtube.com/watch?v=CBXgVaBFyeY",
            duration: "2:33",
        },
        {
            title: "Women's Money In The Bank Ladder Match",
            urlToImage: "/2k25-5.jpg",
            url: "https://www.youtube.com/watch?v=5RchjEwj5mM&t=1s",
            duration: "28:40",
        },
        {
            title: "John Cena vs Roman Reigns",
            urlToImage: "/2k25-6.jpg",
            url: "https://www.youtube.com/watch?v=IZJV-lUg9x4",
            duration: "16:24",
        }
    ];

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        // Simulate API call
        setTimeout(() => {
            setNewsData(mockNewsData);
            setLoading(false);
        }, 1000);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const handleInput = (e) => {
        setSearch(e.target.value)
    }

    const getData = () => {
        setLoading(true);
        setTimeout(() => {
            setNewsData(mockNewsData.filter(item => 
                item.title.toLowerCase().includes(search.toLowerCase())
            ));
            setLoading(false);
        }, 500);
    };

    const VideoCard = ({ data }) => (
        <a
            href={data.url}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: 'none', display: 'block' }}
        >
            <div style={{
                background: '#2a2a2a',
                borderRadius: '8px',
                overflow: 'hidden',
                cursor: 'pointer',
                transition: 'transform 0.2s',
                minWidth: '280px',
                maxWidth: '320px'
            }}>
                <div style={{ position: 'relative' }}>
                    <img 
                        src={data.urlToImage} 
                        alt={data.title}
                        style={{
                            width: '100%',
                            height: '180px',
                            objectFit: 'cover'
                        }}
                    />
                    <div style={{
                        position: 'absolute',
                        bottom: '8px',
                        right: '8px',
                        background: 'rgba(0,0,0,0.8)',
                        color: 'white',
                        padding: '2px 6px',
                        borderRadius: '4px',
                        fontSize: '12px'
                    }}>
                        {data.duration}
                    </div>
                </div>
                <div style={{
                    padding: '12px',
                    color: 'white'
                }}>
                    <h3 style={{
                        margin: '0 0 8px 0',
                        fontSize: '14px',
                        fontWeight: '600',
                        lineHeight: '1.3'
                    }}>
                        {data.title}
                    </h3>
                    <p style={{
                        margin: '0',
                        fontSize: '12px',
                        color: '#ccc',
                        opacity: '0.8'
                    }}>
                        Official
                    </p>
                </div>
            </div>
        </a>
    );

    const navItems = ['NEWS', 'TICKETS', 'MEDIAKIT', 'SHOWS', 'SHOP'];

    return (
        <div style={{
            background: '#000',
            minHeight: '100vh',
            color: 'white',
            fontFamily: 'Arial, sans-serif'
        }}>
            {/* Header */}
            <header style={{
                background: '#1a1a1a',
                padding: '12px 20px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                borderBottom: '1px solid #333'
            }}>
                <div style={{
                    display: 'flex',
                    alignItems: 'center'
                }}>
                    <div style={{
                        marginRight: '20px',
                        display: 'flex',
                        alignItems: 'center'
                    }}>
                        <img 
                            src="/RBID.jpg" 
                            alt="RBID"
                            style={{
                                height: '40px',
                                width: 'auto',
                                borderRadius: '4px'
                            }}
                        />
                    </div>
                    <h1 style={{
                        margin: '0',
                        fontSize: '24px',
                        fontWeight: 'bold',
                        color: '#D32F2F'
                    }}>
                        BURNITDOWN
                    </h1>
                </div>

                {/* Desktop Navigation */}
                {!isMobile && (
                    <>
                        <nav style={{ display: 'flex', gap: '20px' }}>
                            {navItems.map((item) => {
                                const commonStyles = {
                                    color: 'white',
                                    padding: '10px 20px',
                                    fontSize: '14px',
                                    fontWeight: 600,
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                    background: item === 'NEWS' ? '#c41e3a' : 'transparent',
                                    border: 'none',
                                    textDecoration: 'none',
                                    display: 'inline-block'
                                };

                                if (item === 'MEDIAKIT') {
                                    return (
                                        <a
                                            key={item}
                                            href="/Media-kit.pdf"
                                            download="Media-kit.pdf"
                                            style={commonStyles}
                                        >
                                            {item}
                                        </a>
                                    );
                                } else {
                                    return (
                                        <button
                                            key={item}
                                            style={commonStyles}
                                            onClick={() => console.log(item, 'clicked')}
                                        >
                                            {item}
                                        </button>
                                    );
                                }
                            })}
                        </nav>

                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '10px'
                        }}>
                            <input
                                type="text"
                                placeholder="Search"
                                value={search}
                                onChange={handleInput}
                                style={{
                                    padding: '8px 12px',
                                    background: '#2a2a2a',
                                    border: '1px solid #444',
                                    borderRadius: '4px',
                                    color: 'white',
                                    fontSize: '14px'
                                }}
                            />
                            <button
                                onClick={getData}
                                style={{
                                    padding: '8px 16px',
                                    background: '#c41e3a',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                    fontSize: '14px',
                                    fontWeight: '600',
                                }}
                            >
                                Search
                            </button>
                        </div>
                    </>
                )}

                {/* Mobile Hamburger Menu */}
                {isMobile && (
                    <button
                        onClick={() => setIsMenuOpen(!isMenuOpen)}
                        style={{
                            background: 'none',
                            border: 'none',
                            color: 'white',
                            fontSize: '24px',
                            cursor: 'pointer',
                            padding: '5px'
                        }}
                    >

                    </button>
                )}
            </header>

            {/* Mobile Menu Overlay */}
            {isMobile && isMenuOpen && (
                <div style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(0,0,0,0.9)',
                    zIndex: 1000,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '20px'
                }}>
                    <button
                        onClick={() => setIsMenuOpen(false)}
                        style={{
                            position: 'absolute',
                            top: '20px',
                            right: '20px',
                            background: 'none',
                            border: 'none',
                            color: 'white',
                            fontSize: '30px',
                            cursor: 'pointer'
                        }}
                    >

                    </button>

                    {navItems.map((item) => {
                        const commonStyles = {
                            color: 'white',
                            padding: '15px 30px',
                            fontSize: '18px',
                            fontWeight: 600,
                            cursor: 'pointer',
                            background: item === 'NEWS' ? '#c41e3a' : 'transparent',
                            border: 'none',
                            textDecoration: 'none',
                            display: 'block',
                            borderRadius: '4px'
                        };

                        if (item === 'MEDIAKIT') {
                            return (
                                <a
                                    key={item}
                                    href="/Media-kit.pdf"
                                    download="Media-kit.pdf"
                                    style={commonStyles}
                                    onClick={() => setIsMenuOpen(false)}
                                >
                                    {item}
                                </a>
                            );
                        } else {
                            return (
                                <button
                                    key={item}
                                    style={commonStyles}
                                    onClick={() => {
                                        console.log(item, 'clicked');
                                        setIsMenuOpen(false);
                                    }}
                                >
                                    {item}
                                </button>
                            );
                        }
                    })}

                    <div style={{
