import React, { useEffect, useState } from 'react';

const News = () => {
    const [search, setSearch] = useState("");
    const [newsData, setNewsData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState('all');

    // Mock news data that matches wrestling theme
    const mockNewsData = [
        {
            id: 1,
            title: "Major Championship Changes Shake Up the Wrestling World",
            content: "Unprecedented developments in the championship landscape have left fans and competitors alike stunned as new alliances form and rivalries intensify.",
            category: "breaking",
            image: "N.png",
            author: "Wrestling News",
            timeAgo: "Just now",
            views: "1.5K views",
            featured: true
        },
        {
            id: 2,
            title: "Championship Match Results: New Champion Crowned",
            content: "After an intense battle, we have a new champion who dominated the ring with skill and raw power.",
            category: "results",
            image: "/K.png",
            author: "Match Reporter",
            timeAgo: "2 min ago",
            views: "2.3K views",
            featured: false
        },
        {
            id: 3,
            title: "Road to WrestleMania Campaign Unveiled",
            content: "WWE pulls back the curtain on its “Road to WrestleMania” ad campaign—see the bold visuals and star-driven spots set to build momentum for the biggest show of the year.",
            category: "behind-scenes",
            image: "E.png",
            author: "Insider Report",
            timeAgo: "5 min ago",
            views: "890 views",
            featured: false
        },
        {
            id: 4,
            title: "Upcoming Events: What to Expect This Season",
            content: "A comprehensive preview of the wrestling matches and key storylines for the coming months.",
            category: "upcoming",
            image: "T.png",
            author: "Event Preview",
            timeAgo: "15 min ago",
            views: "1.2K views",
            featured: false
        },
        {
            id: 5,
            title: "Superstars Amp Up ‘Road to WrestleMania’ Hype",
            content: "Hear how WWE’s biggest names are helping promote the “Road to WrestleMania” across social and live events.",
            category: "breaking",
            image: "D.png",
            author: "Wrestling News",
            timeAgo: "1 hour ago",
            views: "5.7K views",
            featured: false
        },
        {
            id: 6,
            title: "New Wrestling Academy Opens Its Doors",
            content: "State-of-the-art training facility aims to develop the next generation of wrestling superstars.",
            category: "behind-scenes",
            image: "/news-6.jpg",
            author: "Training Report",
            timeAgo: "2 hours ago",
            views: "756 views",
            featured: false
        }
    ];

    // Category mapping for filtering
    const categories = [
        { id: 'all', label: 'All News', color: '#522D92' },
        { id: 'breaking', label: 'Breaking News', color: '#ff4444' },
        { id: 'results', label: 'Match Results', color: '#44ff44' },
        { id: 'behind-scenes', label: 'Behind the Scenes', color: '#4444ff' },
        { id: 'upcoming', label: 'Upcoming Events', color: '#ff8844' }
    ];

    useEffect(() => {
        // Simulate loading news data
        setTimeout(() => {
            setNewsData(mockNewsData);
            setLoading(false);
        }, 1000);
    }, []);

    // Filter news based on search and category
    const filteredNews = newsData.filter(item => {
        const matchesSearch = item.title.toLowerCase().includes(search.toLowerCase()) ||
                             item.content.toLowerCase().includes(search.toLowerCase());
        const matchesCategory = selectedCategory === 'all' || item.category === selectedCategory;
        return matchesSearch && matchesCategory;
    });

    const handleSearch = () => {
        // Search functionality is handled by the filter above
        setLoading(true);
        setTimeout(() => setLoading(false), 500);
    };

    const navItems = ['HOME', 'TICKETS', 'MEDIAKIT', 'SHOWS', 'SHOP'];

    // Navigation component that handles both desktop and mobile
    const NavLinks = ({ isMobile = false }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
            {navItems.map((item) => {
                if (item === 'MEDIAKIT') {
                    return (
                        <a
                            key={item}
                            href="Media-kit.pdf"
                            download="mediakit.pdf"
                            className="nav-link"
                            onClick={() => isMobile && setIsMobileMenuOpen(false)}
                        >
                            {item}
                        </a>
                    );
                }
                return (
                    <button
                        key={item}
                        className="nav-link"
                        onClick={() => {
                            console.log(item, 'clicked');
                            if (isMobile) setIsMobileMenuOpen(false);
                        }}
                    >
                        {item}
                    </button>
                );
            })}
        </nav>
    );

    // Featured news card component for the main highlight
    const FeaturedNewsCard = ({ article }) => (
        <div className="featured-news-card">
            <div className="featured-image-container">
                <img src={article.image} alt={article.title} className="featured-image" />
                <div className="featured-badge">FEATURED</div>
            </div>
            <div className="featured-content">
                <span className="featured-category">{categories.find(cat => cat.id === article.category)?.label}</span>
                <h2 className="featured-title">{article.title}</h2>
                <p className="featured-excerpt">{article.content}</p>
                <div className="featured-meta">
                    <span className="author">📝 {article.author}</span>
                    <span className="time">⏰ {article.timeAgo}</span>
                    <span className="views">👁️ {article.views}</span>
                </div>
            </div>
        </div>
    );

    // Regular news card component for the grid layout
    const NewsCard = ({ article }) => (
        <div className="news-card">
            <div className="news-image-container">
                <img src={article.image} alt={article.title} className="news-image" />
                <div className="news-category-badge" style={{backgroundColor: categories.find(cat => cat.id === article.category)?.color}}>
                    {categories.find(cat => cat.id === article.category)?.label}
                </div>
            </div>
            <div className="news-content">
                <h3 className="news-title">{article.title}</h3>
                <p className="news-excerpt">{article.content}</p>
                <div className="news-meta">
                    <span className="news-author">{article.author}</span>
                    <span className="news-time">{article.timeAgo}</span>
                    <span className="news-views">{article.views}</span>
                </div>
            </div>
        </div>
    );

    return (
        <>
            {/* Comprehensive CSS styles for the news page */}
            <style>{`
                :root {
                    --main-red: #522D92;
                    --dark-bg: #000;
                    --primary-bg: #111;
                    --secondary-bg: #1a1a1a;
                    --tertiary-bg: #2a2a2a;
                    --border-color: #333;
                    --text-light: #fff;
                    --text-dark: #ccc;
                    --text-muted: #666;
                    --gradient-purple: linear-gradient(135deg, #522D92 0%, #341C5C 100%);
                }

                * {
                    box-sizing: border-box;
                }

                body {
                    margin: 0;
                    background-color: var(--dark-bg);
                    color: var(--text-light);
                    font-family: 'Roboto', 'Arial', sans-serif;
                    line-height: 1.6;
                }

                .app-container {
                    background-color: var(--dark-bg);
                    min-height: 100vh;
                }

                /* Header Styles */
                .app-header {
                    background-color: var(--secondary-bg);
                    padding: 1rem 2rem;
                    border-bottom: 1px solid var(--border-color);
                    position: sticky;
                    top: 0;
                    z-index: 100;
                }

                .header-content {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    max-width: 1800px;
                    margin: 0 auto;
                }
                
                .logo-container {
                    display: flex;
                    align-items: center;
                    gap: 1rem;
                }

                .logo-img {
                    height: 40px;
                    border-radius: 4px;
                }

                .logo-title {
                    margin: 0;
                    font-size: 1.75rem;
                    font-weight: bold;
                    color: var(--main-red);
                    text-transform: uppercase;
                    font-family: 'League Gothic', Impact, sans-serif;
                }

                .desktop-nav {
                    display: none;
                }
                
                .header-search-container {
                    display: none;
                }

                .hamburger-btn {
                    background: none;
                    border: none;
                    color: var(--text-light);
                    font-size: 2rem;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: absolute;
                    top: 74px;
                    left: 0;
                    right: 0;
                    background: var(--secondary-bg);
                    border-bottom: 1px solid var(--border-color);
                    z-index: 1000;
                    padding: 1.5rem;
                    display: flex;
                    flex-direction: column;
                    gap: 1rem;
                }
                
                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 0.5rem;
                }

                .mobile-search-container {
                    display: flex;
                    gap: 0.5rem;
                    margin-top: 1rem;
                }

                /* Navigation Links */
                .nav-link {
                    color: var(--text-light);
                    padding: 0.75rem 1rem;
                    font-size: 1rem;
                    font-weight: 600;
                    border-radius: 4px;
                    cursor: pointer;
                    background: transparent;
                    border: none;
                    text-decoration: none;
                    text-align: left;
                    transition: all 0.2s ease;
                }

                .nav-link:hover {
                    background-color: var(--tertiary-bg);
                    transform: translateY(-1px);
                }

                /* Search Elements */
                .search-input {
                    padding: 0.75rem 1rem;
                    background: var(--tertiary-bg);
                    border: 1px solid #444;
                    border-radius: 4px;
                    color: var(--text-light);
                    font-size: 1rem;
                    flex-grow: 1;
                    transition: border-color 0.2s ease;
                }

                .search-input:focus {
                    outline: none;
                    border-color: var(--main-red);
                }

                .search-btn {
                    padding: 0.75rem 1.5rem;
                    background: var(--main-red);
                    color: var(--text-light);
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    font-size: 1rem;
                    font-weight: 600;
                    transition: all 0.2s ease;
                }

                .search-btn:hover {
                    background-color: #341C5C;
                    transform: translateY(-1px);
                }

                /* Hero Section */
                .news-hero {
                    background: var(--gradient-purple);
                    padding: 4rem 2rem;
                    text-align: center;
                    margin-bottom: 2rem;
                }

                .news-hero h1 {
                    font-size: 3rem;
                    font-weight: bold;
                    margin: 0 0 1rem 0;
                    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
                }

                .news-hero p {
                    font-size: 1.2rem;
                    opacity: 0.9;
                    margin: 0 0 2rem 0;
                    max-width: 600px;
                    margin-left: auto;
                    margin-right: auto;
                }

                .breaking-news-btn {
                    background: #341C5C;
                    color: white;
                    border: none;
                    padding: 1rem 2rem;
                    border-radius: 25px;
                    font-size: 1rem;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                }

                .breaking-news-btn:hover {
                    background: #cc3333;
                    transform: translateY(-2px);
                    box-shadow: 0 5px 15px rgba(255, 68, 68, 0.3);
                }

                /* Main Content */
                .main-content {
                    padding: 0 2rem 2rem;
                    max-width: 1800px;
                    margin: 0 auto;
                }

                /* Category Filter */
                .category-filter {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 1rem;
                    margin-bottom: 2rem;
                    justify-content: center;
                }

                .category-btn {
                    padding: 0.75rem 1.5rem;
                    background: var(--tertiary-bg);
                    color: var(--text-light);
                    border: 2px solid transparent;
                    border-radius: 25px;
                    cursor: pointer;
                    font-size: 0.9rem;
                    font-weight: 600;
                    transition: all 0.2s ease;
                    text-transform: uppercase;
                    letter-spacing: 0.5px;
                }

                .category-btn:hover,
                .category-btn.active {
                    background: var(--main-red);
                    border-color: var(--main-red);
                    transform: translateY(-1px);
                }

                /* Featured News Section */
                .featured-section {
                    margin-bottom: 3rem;
                }

                .featured-news-card {
                    background: var(--secondary-bg);
                    border-radius: 12px;
                    overflow: hidden;
                    border: 1px solid var(--border-color);
                    transition: transform 0.3s ease;
                }

                .featured-news-card:hover {
                    transform: translateY(-5px);
                }

                .featured-image-container {
                    position: relative;
                    height: 300px;
                    overflow: hidden;
                }

                .featured-image {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    transition: transform 0.3s ease;
                }

                .featured-news-card:hover .featured-image {
                    transform: scale(1.05);
                }

                .featured-badge {
                    position: absolute;
                    top: 1rem;
                    left: 1rem;
                    background: #522D92;
                    color: white;
                    padding: 0.5rem 1rem;
                    border-radius: 15px;
                    font-size: 0.8rem;
                    font-weight: bold;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                }

                .featured-content {
                    padding: 2rem;
                }

                .featured-category {
                    color: var(--main-red);
                    font-size: 0.9rem;
                    font-weight: 600;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                }

                .featured-title {
                    font-size: 2rem;
                    font-weight: bold;
                    margin: 1rem 0;
                    line-height: 1.2;
                }

                .featured-excerpt {
                    font-size: 1.1rem;
                    color: var(--text-dark);
                    margin-bottom: 1.5rem;
                    line-height: 1.6;
                }

                .featured-meta {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 1.5rem;
                    font-size: 0.9rem;
                    color: var(--text-muted);
                }

                /* News Grid */
                .news-grid {
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;
                    margin-bottom: 3rem;
                }

                .news-card {
                    background: var(--secondary-bg);
                    border-radius: 8px;
                    overflow: hidden;
                    border: 1px solid var(--border-color);
                    transition: all 0.3s ease;
                    cursor: pointer;
                }

                .news-card:hover {
                    transform: translateY(-3px);
                    box-shadow: 0 10px 30px rgba(82, 45, 146, 0.2);
                }

                .news-image-container {
                    position: relative;
                    height: 200px;
                    overflow: hidden;
                }

                .news-image {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    transition: transform 0.3s ease;
                }

                .news-card:hover .news-image {
                    transform: scale(1.1);
                }

                .news-category-badge {
                    position: absolute;
                    top: 1rem;
                    left: 1rem;
                    color: white;
                    padding: 0.25rem 0.75rem;
                    border-radius: 12px;
                    font-size: 0.75rem;
                    font-weight: 600;
                    text-transform: uppercase;
                }

                .news-content {
                    padding: 1.5rem;
                }

                .news-title {
                    font-size: 1.25rem;
                    font-weight: 600;
                    margin: 0 0 1rem 0;
                    line-height: 1.3;
                }

                .news-excerpt {
                    color: var(--text-dark);
                    margin-bottom: 1rem;
                    line-height: 1.5;
                }

                .news-meta {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 1rem;
                    font-size: 0.8rem;
                    color: var(--text-muted);
                }

                .news-author {
                    font-weight: 600;
                }

                /* Loading State */
                .loading {
                    text-align: center;
                    padding: 3rem;
                    font-size: 1.2rem;
                    color: var(--text-dark);
                }

                /* Footer Styles */
                .app-footer {
                    background: var(--primary-bg);
                    padding: 3rem 2rem;
                    border-top: 1px solid var(--border-color);
                    margin-top: 3rem;
                }

                .footer-content {
                    max-width: 1800px;
                    margin: 0 auto;
                    display: grid;
                    grid-template-columns: 1fr;
                    gap: 2rem;
                }

                .footer-brand h3 {
                    margin: 0 0 1rem 0;
                    font-size: 1.5rem;
                    color: var(--main-red);
                    font-family: 'League Gothic', Impact, sans-serif;
                }

                .footer-brand p {
                    margin: 0 0 1.5rem 0;
                    color: var(--text-dark);
                    line-height: 1.5;
                }

                .social-links {
                    display: flex;
                    gap: 1rem;
                    flex-wrap: wrap;
                }

                .social-link {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    padding: 0.75rem 1rem;
                    border-radius: 8px;
                    text-decoration: none;
                    color: var(--text-light);
                    font-weight: 600;
                    transition: all 0.2s ease;
                }

                .social-link.facebook { background: #1877f2; }
                .social-link.youtube { background: #ff0000; }
                .social-link.instagram { background: #e4405f; }

                .social-link:hover {
                    transform: translateY(-2px);
                    box-shadow: 0 5px 15px rgba(0,0,0,0.3);
                }

                .social-icon {
                    width: 20px;
                    height: 20px;
                    filter: brightness(0) invert(1);
                }

                .footer-copyright {
                    border-top: 1px solid var(--border-color);
                    margin-top: 2rem;
                    padding-top: 2rem;
                    text-align: center;
                    color: var(--text-muted);
                    font-size: 0.9rem;
                }

                /* Responsive Design */
                @media (min-width: 768px) {
                    .news-grid {
                        grid-template-columns: repeat(2, 1fr);
                    }
                    
                    .featured-news-card {
                        display: grid;
                        grid-template-columns: 1fr 1fr;
                        align-items: center;
                    }
                    
                    .featured-image-container {
                        height: 400px;
                    }
                    
                    .category-filter {
                        justify-content: flex-start;
                    }
                    
                    .footer-content {
                        grid-template-columns: 2fr 1fr;
                        align-items: start;
                    }
                }

                @media (min-width: 1024px) {
                    .hamburger-btn {
                        display: none;
                    }
                    
                    .desktop-nav {
                        display: flex;
                        align-items: center;
                        gap: 1rem;
                        margin-left: 2rem;
                    }
                    
                    .header-search-container {
                        display: flex;
                        align-items: center;
                        gap: 0.5rem;
                        margin-left: auto;
                    }
                    
                    .news-grid {
                        grid-template-columns: repeat(3, 1fr);
                    }
                    
                    .news-hero h1 {
                        font-size: 4rem;
                    }
                }

                @media (min-width: 1400px) {
                    .news-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }
                }
            `}</style>

            <div className="app-container">
                {/* Header Section */}
                <header className="app-header">
                    <div className="header-content">
                        <div className="logo-container">
                            <img src="PBID.jpg" alt="Burn It Down Logo" className="logo-img" />
                            <h1 className="logo-title">BURNITDOWN</h1>
                        </div>

                        {/* Desktop Navigation */}
                        <NavLinks />
                        
                        {/* Desktop Search */}
                        <div className="header-search-container">
                            <input
                                type="text"
                                placeholder="Search News..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                                className="search-input"
                            />
                            <button onClick={handleSearch} className="search-btn">Search</button>
                        </div>

                        {/* Mobile Menu Button */}
                        <button className="hamburger-btn" onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
                            &#9776;
                        </button>
                    </div>
                </header>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <NavLinks isMobile={true} />
                        <div className="mobile-search-container">
                            <input
                                type="text"
                                placeholder="Search News..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                className="search-input"
                            />
                            <button onClick={handleSearch} className="search-btn">Search</button>
                        </div>
                    </div>
                )}

                {/* Hero Section */}
                <section className="news-hero">
                    <h1>Latest News</h1>
                    <p>Stay updated with the latest wrestling news, results, and behind-the-scenes content</p>
                    <button className="breaking-news-btn" onClick={() => setSelectedCategory('breaking')}>
                        Breaking News
                    </button>
                </section>

                <main className="main-content">
                    {/* Category Filter */}
                    <div className="category-filter">
                        {categories.map(category => (
                            <button
                                key={category.id}
                                className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category.id)}
                            >
                                {category.label}
                            </button>
                        ))}
                    </div>

                    {loading ? (
                        <div className="loading">Loading latest news...</div>
                    ) : (
                        <>
                            {/* Featured News Section */}
                            {filteredNews.find(news => news.featured) && (
                                <div className="featured-section">
                                    <FeaturedNewsCard article={filteredNews.find(news => news.featured)} />
                                </div>
                            )}

                            {/* News Grid */}
                            <div className="news-grid">
                                {filteredNews.filter(news => !news.featured).map(article => (
                                    <NewsCard key={article.id} article={article} />
                                ))}
                            </div>

                            {filteredNews.length === 0 && (
                                <div className="loading">
                                    No news articles found matching your search criteria.
                                </div>
                            )}
                        </>
                    )}
                </main>

                {/* Footer */}
                <footer className="app-footer">
                    <div className="footer-content">
                        <div className="footer-brand">
                            <h3>BURNITDOWN</h3>
                            <p>Your ultimate destination for wrestling entertainment, news, and exclusive content. Stay connected with all the latest updates from the wrestling world.</p>
                            <div className="social-links">
                                <a href="https://www.facebook.com/share/g/1BpZVnNsqr/?mibextid=wwXIfr" className="social-link facebook" target="_blank" rel="noopener noreferrer">
                                    <img src="facebook.png" alt="Facebook" className="social-icon" />
                                    Facebook
                                </a>
                                <a href="https://youtube.com/@burnitdownyt?si=ogbNfYLps54zRZQ1" className="social-link youtube" target="_blank" rel="noopener noreferrer">
                                    <img src="youtube.png" alt="YouTube" className="social-icon" />
                                    YouTube
                                </a>
                                <a href="https://www.instagram.com/burnitdownyt?igsh=MTExOGNwOHJhZWYyYQ%3D%3D&utm_source=qr" className="social-link instagram" target="_blank" rel="noopener noreferrer">
                                    <img src="instagram.png" alt="Instagram" className="social-icon" />
                                    Instagram
                                </a>
                            </div>
                        </div>
                    </div>
                    <div className="footer-copyright">
                        &copy; 2025 Burn It Down. All rights reserved.
                    </div>
                </footer>
            </div>
        </>
    );
};

export default News;
