import { useState, useRef } from 'react';

const BIDapp= () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);

    const totalPages = Math.ceil(items.length / itemsPerView); // itemsPerView = 4



    // Refs for scrolling to sections
    const homeRef = useRef(null);
    const shopRef = useRef(null);
    const latestNewsRef = useRef(null);
    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = '';
        link.download = '';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setIsMobileMenuOpen(false);
    };

    // Safe icon components using Unicode/HTML entities
    const MenuIcon = () => <span style={{fontSize: '20px'}}>☰</span>;
    const CloseIcon = () => <span style={{fontSize: '20px'}}>✕</span>;
    const UserIcon = ({ isMobile = false }) => (<button
    style={{
      background: 'none',
      border: 'none',
      padding: 0,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      filter: isMobile ? 'invert(1)' : 'none',
    }}
    aria-label="User"
  >
    <img src="user.png" alt="User Icon" style={{ width: '20px' }} />
  </button>
);
    const ShoppingBagIcon = () => (
  <button
    style={{
      background: 'none',
      border: 'none',
      padding: 0,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}
    aria-label="Shopping Bag"
  >
    <img src="shoppingbag.png" alt="Shopping Icon" style={{ width: '20px' }} />
  </button>
);

    const NavLinks = ({ isMobile = false, onItemClick }) => (
        <nav className={isMobile ? 'mobile-nav' : 'desktop-nav'}>
            <button onClick={() => onItemClick ? onItemClick(homeRef) : scrollToSection(homeRef)} className="nav-link">
                Home
            </button>
            <button onClick={() => onItemClick ? onItemClick(shopRef) : scrollToSection(shopRef)} className="nav-link">
                →Shop 
            </button>
            <button onClick={() => onItemClick ? onItemClick(latestNewsRef) : scrollToSection(latestNewsRef)} className="nav-link">
                →Latest News
            </button>
            <button onClick={handleMediaKitDownload} className="nav-link">
                Media Kit
            </button>
            <button onClick={() => console.log('Contact Us clicked')} className="nav-link">
                Contact Us
            </button>
             {isMobile && (
              <div className="mobile-user-icon">
            <UserIcon/>
            </div>
            )}
        </nav>
    );

    return (
        <>
            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                body {
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    background-color: #000;
                    color: white;
                    line-height: 1.6;
                }

                .container {
                    min-height: 100vh;
                    background-color: #000;
                }
                .nav-item {
                    display: inline-flex;
                    align-items: center;
                    gap: -122px; /* Controls the spacing between arrow and text */
}
                    .nav-arrow {
                     margin-right: -45px; /* pulls the image closer to the button */
                     vertical-align: middle; /* aligns the arrow vertically with the text */
                    
                    
                
                }

                /* Discount Banner */
                .discount-banner {
                    background-color: black;
                    color: white;
                    text-align: center;
                    padding: 0.5rem;
                    font-size: 0.85rem;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 0.3rem;
                    position: relative;
                }

                .discount-banner .discount-icon {
                    display:flex;
                    align-items:center;
                    
            }
                .discount-close {
                    position: relative;
                    right: -2rem;
                    background: none;
                    border: none;
                    color: white;
                    cursor: pointer;
                    font-size: 18px;
                    padding: 0.2rem;
                    display: flex;
                    align-items: center;
                    justify-content: center;
}

.discount-close:hover {
    color: #ccc;
}

                /* Header Styles */
                .header {
                    background-color: white;
                    border-bottom: 1px solid #e0e0e0;
                    padding: 0.8rem 0;
                    position: sticky;
                    top: 0;
                    z-index: 1000;
                }

                .header-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    background-color: white;
                    position: relative;
                }


                .logo {
                    font-size: 1.4rem;
                    font-weight: bold;
                    color: #000;
                    text-decoration: none;
                    background-color: white;
                    flex: 1;
                    margin-left: 0.5rem;
                    
                }

                .desktop-nav {
                    display: none;
                    gap: 2.5rem;
                    background-color: white;
                    position: absolute;
                    left: 50%;
                    transform: translateX(-50%);
                      z-index: 10;
                }

                .nav-link {
                    background: none;
                    border: none;
                    color: Black;
                    background-color: white;
                    text-decoration: none;
                    font-size: 0.9rem;
                    cursor: pointer;
                    padding: 0.5rem 0;
                    font-weight: 500;
                    transition: color 0.3s;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    white-space: nowrap;
                }
                .nav-link img {
                   display: inline-block;
                   width: 1rem;
                   height: auto;
                   margin: 0;
                }
       

                .nav-link:hover {
                    color: #000;
                    background-color: white;
                }
                
                .nav-link1.no-underline {
                   text-decoration: none;
                
                }
                    @media (max-width: 768px) {
  .header-right {
    display: none;
  }
}

                .header-right {
                    display: flex;
                    align-items: center;
                    gap: 0.2rem;
                    background-color: white;
                    flex: 1; /* Take up available space */
                    justify-content: flex-end;
                }

                .header-icon {
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                    border-radius: 4px;
                    transition: background-color 0.3s;
                }

                .header-icon:hover {
                    background-color: #f5f5f5;
                }
                    .desktop-only {
  display: inline-flex;
}

@media (max-width: 768px) {
  .desktop-only {
    display: none !important;
  }
}

                .mobile-menu-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 10;
                    background-color: white;
                    z-index: 2000;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                }

                .mobile-menu-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 2rem;
                }

                .mobile-menu-header .logo {
                    color: white;
                }

                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem;
                    background-color: white;
                    color: white;
                }

                .mobile-nav .nav-link {
                    font-size: 1.1rem;
                    padding: 1rem 0;
                    border-bottom: white;
                    text-align: center;
                    color: black;
                }

                .close-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Hero Section */
                .hero {
                    background: #000;
                    padding: 2rem 0;
                    text-align: center;
                    position: relative;
                }

                .hero-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .hero-logo {
                    width: 100%;
                    max-width: 800px;
                    height: 400px;
                    margin: 0 auto;
                    background-color: black;
                    border-radius: 8px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #666;
                    font-size: 1rem;
                }
                    .hero-logo img {
                    width: 100%;
                    height: 76%;
                    display:block;
                    object-fit: cover;
                    object-position: center;
                }

                .tickets-btn {
                    position: absolute;
                    top: 2rem;
                    right: 8.8rem;
                    background-color: #fff;
                    color: #000;
                    border: none;
                    padding: 0.7rem 1.2rem;
                    border-radius: 4px;
                    font-weight: 600;
                    cursor: pointer;
                    transition: transform 1s;
                    font-size: 0.9rem;
                }

                .tickets-btn:hover {
                    transform: translateY(-1px);
                }

                /* Latest News Section */
                .latest-news {
                    padding: 3rem 0;
                    background-color: white;
                }

                .section-container {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .section-title {
                    font-size: 1.8rem;
                    margin-bottom: 2rem;
                    color: #000;
                    font-weight: 600;
                    gap: 0.5rem;
                }
                .section-arrow {
    font-size: 1.2rem;
    color: #666;
    cursor: pointer;
    transition: color 0.3s;
}

                .news-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
                    gap: 1.5rem;
                    grid-auto-flow: row;
                }

                .news-card {
                    background-color: transparent;
                    border-radius: 8px;
                    overflow: hidden;
                    box-shadow: none;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                    position: relative;
                }

                .news-card:hover {
                    transform: translateY(-4px);
                    box-shadow: 0 4px 16px rgba(255, 255,255,1);
                }
                .news-image {
                    width: 100%;
                    height: 420px;
                    object-fit: cover;
                    background-color: transparent;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #999;
                    font-size: 0.85rem;
                    border-bottom: none;
                }
                
                .news-image img {
                   width: 100%;
                   heigh:100%
                   object-fit:cover
                }

                .news-content {
                    padding: 1.2rem;
                }

                .news-label {
                    /* Removed position: absolute; top; left; */
                background-color: white;
                color: black;
                padding: 0.25rem 0.6rem;
                border-radius: 12px;
                font-size: 0.75rem;
                font-weight: 600;
                display: inline-block; /* Changed to inline-block */
                margin-bottom: 0.7rem; /* Added margin-bottom for spacing */
                text-transform: uppercase;
                position: absolute; /* Added back for specific placement within the card as seen in HOMEPAGE.jpg */
                top: 12px; /* Adjusted as per HOMEPAGE.jpg */
                left: 12px; /* Adjusted as per HOMEPAGE.jpg */
                z-index: 10;
                }

                .news-title {
                    font-size: 1rem;
                    margin: 0;
                    line-height: 1.4;
                    color: #000;
                    font-weight: 600;
                }

                /* Shop Section */
                .shop {
                    padding: 3rem 0;
                    background-color: white;
                }

                .shop .section-title {
                    color: #000;
                }

                .shop-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
                    gap: 2rem;
                    margin-bottom: 3rem;
                }

                .product-card {
                    background-color: #fff;
                    border: 1px solid white;
                    border-radius: 8px;
                    overflow: hidden;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                }

                .product-card:hover {
                    transform: translateY(-4px);
                    
                }

                .product-image {
                    width: 100%;
                    height:400px;
                    background-color: white;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #999;
                    position: relative;
                    border-bottom: 1px solid white;
                    font-size: 0.85rem;
                }
                .product-image img {
                    width:100%;
                    height:100%;
                    object-fit:cover;
                    object-position: center;
                }

                .hot-badge {
                    position: absolute;
                    top: 12px;
                    left: 12px;
                    background-color: white;
                    color: black;
                    padding: 0.3rem 0.7rem;
                    border-radius: 12px;
                    font-size: 0.75rem;
                    font-weight: 600;
                    text-transform: uppercase;
                }

                .product-info {
                    padding: 1.2rem;
                }

                .product-title {
                    font-size: 1rem;
                    margin-bottom: 0.7rem;
                    line-height: 1.4;
                    color: #000;
                    font-weight: 500;
                }

                .product-price {
                    color: #000;
                    font-weight: 600;
                    font-size: 1.1rem;
                }



                /* Brand Highlights */
                .brand-highlights {
                    padding: 3rem 0;
                    background-color: white;
                }

                .brand-highlights .section-title {
                    color: #000;
                    text-align: center;
                }

                .brand-subtitle {
                    color: #666;
                    text-align: center;
                    margin-bottom: 2rem;
                    font-size: 1rem;
                }

                .highlights-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
                    gap: 2rem;
                    margin-top: 2rem;
                }

                .highlight-card {
                    background-color: transparent;
                    border: none;
                    border-radius: 0;
                    padding: 0;
                    flex-direction: column;
                    display:flex;
                    transition: transform 0.3s, box-shadow 0.3s;
                    cursor: pointer;
                }

                .highlight-card:hover {
                    transform: translateY(-4px);
                    box-shadow: 0 4px 16px rgba(0,0,0,0.15);
                }

                .highlight-icon {
                    width: 100%;
                    aspect-ratio: 1/1;
                    overflow: hidden;
                    display: block;
                    margin: 0;

                    
                }
                .highlight-icon img {
                  width: 100%;
                  height: 100%;
               object-fit: cover;  /* or cover if you want to fill the box */
                 object-position:center;
                 display: block;
}

                /* Footer */
                .footer {
                    background-color: black;
                    padding: 3rem 0 1rem;
                    color: black;
                }

                .footer-content {
                    max-width: 1400px;
                    margin: 0 auto;
                    padding: 0 1rem;
                }

                .footer-nav {
                    display: flex;
                    flex-direction: column;
                    background:black;
                    gap: 1rem;
                    margin-bottom: 2rem;
                    align-items: center;
                }

                .footer-nav .nav-link1 {
                    color: #ccc;
                    font-size: 0.9rem;
                    padding: 0.7rem 0;
                    width: 100%;
                    text-align: center;
                    background-color: black;
                    transition: color 0.3s, background-color 0.3s;
                    text-decoration: none;
                }

                .footer-nav .nav-link1:hover {
                    color: #fff;
                    background-color: black;
                    text-decoration: none;
                }

                .social-icons {
                    display: flex;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 2rem;
                }

                .social-icon {
                    width: 40px;
                    height: 40px;
                    background-color: #333;
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #fff;
                    text-decoration: none;
                    transition: background-color 0.3s;
                    font-size: 1.1rem;
                    background-color: black;
                }

                .social-icon:hover {
                    background-color: #555;
                }

                .footer-text {
                    color: #666;
                    font-size: 0.85rem;
                    text-align: center;
                }
                a {
                    text-decoration:none;
                }
                /* Responsive Design */
                @media (min-width: 768px) {
                    .mobile-menu-btn {
                        display: none;
                    }

                    .desktop-nav, .header-right {
                        display: flex;
                    }

                    .tickets-btn {
                        position: absolute; /* Absolute for desktop*/
                        top: 2rem; /* Position from top*/
                        right: 8.8rem; /* Position from right*/
                        margin-top: 0; /* Reset mobile margin */
                    }

                    .hero-content {
                        padding: 0 2rem;
                    }

                    .footer-nav {
                        flex-direction: row;
                        justify-content: center;
                        flex-wrap: wrap;
                        gap: 2.5rem;
                        background-color: black;
                    }

                    .footer-nav .nav-link1 {
                        width: auto;
                        border-bottom: none;
                        padding: 0.5rem 0;
                        background-color: black;
                        text-decoration: none;

                    }

                    .footer-nav .nav-link1:hover {
                        background-color: black;
                        color: #black;
                        text-decoration: none;
                    }
                   
                }
                

                @media (min-width: 1024px) {
                    .news-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }

                    .shop-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }

                    .highlights-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }
                        .highlights-grid {
            /* Adjust grid template columns if 200px icon size affects overall layout */
            /* You might need to make the columns larger or fewer if the 200px icons are too big */
            grid-template-columns: repeat(auto-fit, minmax(220px, 1fr)); /* Example adjustment for min-width for larger icons */
            gap: 1.5rem; /* Adjust gap as needed */
        }
                }
            `}</style>

            <div className="container">
                {/* Discount Banner → */}
                {isDiscountVisible && (
    <div className="discount-banner">
        <span className="discount-icon"><img src="ticket-percent.png"/></span>
        10% off store—Limited time!
        <button 
            className="discount-close"
            onClick={() => setIsDiscountVisible(false)}
        >
            ✕
        </button>
    </div>
)}

                {/* Header */}
                <header className="header">
                    <div className="header-content">
                        <button 
                            className="mobile-menu-btn"
                            onClick={() => setIsMobileMenuOpen(true)}
                        >
                            <MenuIcon />
                        </button>
                        <p className="logo">BURNITDOWNYT</p>
                        
                        <NavLinks />
                        
                        <div className="header-right">
                            <div className="user-icon desktop-only">
                                <UserIcon />
                            </div>
                            <div className="header-icon">
                                <ShoppingBagIcon />
                            </div>
                        </div>

                        
                    </div>
                </header>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div className="mobile-menu">
                        <div className="mobile-menu-header">
                            <span className="logo">BURNITDOWNYT</span>
                            <button 
                                className="close-btn"
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                <CloseIcon />
                            </button>
                        </div>
                        <NavLinks isMobile={true} onItemClick={scrollToSection} />
                    </div>
                )}

                {/* Hero Section */}
                <section ref={homeRef} className="hero">
                    <div className="hero-content">
                        <div className="hero-logo">
                            <img className="hero-logo img"src="MANIA-HOMECOVER.jpg"/>
                        </div>
                        <button className="tickets-btn">Tickets</button>
                    </div>
                </section>

                {/* Latest News Section */}
                <section ref={latestNewsRef} className="latest-news">
                    <div className="section-container">
                        <h2 className="section-title">Latest News</h2>
                         <div style={{
                        display: 'flex',
                        gap: '8px',
                        alignItems: 'center'
                    }}>
                        {Array.from({ length: newsPages }).map((_, index) => (
                            <button
                                key={index}
                                onClick={() => scrollToNewsPage(index)}
                                style={{
                                    width: '8px',
                                    height: '8px',
                                    borderRadius: '50%',
                                    border: 'none',
                                    background: newsCurrentIndex === index ? 'white' : '#666',
                                    cursor: 'pointer',
                                    transition: 'background-color 0.3s ease',
                                    padding: '0'
                                }}
                            />
                        ))}
                    </div>

                        <div className="news-grid">
                            <div className="news-card">
                            <span className="news-label">NEW</span>
                                <div className="news-image">
                                    <a href=""><img src="instagrampostcover1.jpg"/></a>
                               </div>
                            </div>
                            <div className="news-card">
                                    <span className="news-label">NEW</span>
                                <div className="news-image">
                                    <a href=""><img src="instagrampost2cover.webp"/></a>
                                </div>
                                
                            </div>

                            <div className="news-card">
                            <span className="news-label">NEW</span>
                                <div className="news-image">
                                    <a href=""><img src="instagrampostcover3.jpg"/></a>
                                
                                    
                                </div>
                            </div>
                            

                            <div className="news-card">
                                    <div className="news-label">
                                    <span >NEW</span>
                                    </div>
                                    <div className="news-image">
                                    <a href=""><img src="instagrampostcover4.jpg"/></a>
                                </div>
                            </div>
                    </div>
                    </div>
                </section>

                {/* Shop Section */}
                <section ref={shopRef} className="shop">
                    <div className="section-container">
                        <h2 className="section-title">Shop</h2>
                        <div className="shop-grid">
                            {/* First Row */}
                            <div className="product-card">
                                <div className="product-image">
                                    <span className="hot-badge">HOT</span>
                                    <img src="WWEBELTCENA.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE Undisputed Championship with John Cena side plates</h3>
                                    <div className="product-price">$2199.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <span className="hot-badge">HOT</span>
                                    <img src="WIGNEDEAGLE_.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE Winged Eagle Championship Belt</h3>
                                    <div className="product-price">$1799.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <img src="SPINNERBELTTT.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE Spinner Championship with Custom name plate</h3>
                                    <div className="product-price">$1699.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <img src="WORLDTITLEE.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE Big Gold World Heavyweight Championship</h3>
                                    <div className="product-price">$1799.00</div>
                                </div>
                            </div>

                            {/* Second Row */}
                            <div className="product-card">
                                <div className="product-image">
                                    <img src="UNDISPUTEDTITLE.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE Undisputed Championship with Custom name plate</h3>
                                    <div className="product-price">$1699.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <span className="hot-badge">HOT</span>
                                   <img src="WORLDTITLE.png"/> 
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE New World Heavyweight Championship Belt</h3>
                                    <div className="product-price">$1999.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <img src="CUSTOMSIDEPLATES.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">Custom WWE Side Plates of any WWE Superstar / Custom Design</h3>
                                    <div className="product-price">$349.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <img src="NXTTITLE.png"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE New NXT Championship</h3>
                                    <div className="product-price">$1199.00</div>
                                </div>
                            </div>

                            {/* Third Row */}
                            <div className="product-card">
                                <div className="product-image">
                                    <img src="smoking-skull-title.jpg"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">John Cena 2025 Farewell Tour Poster Autograph</h3>
                                    <div className="product-price">$599.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <span className="hot-badge">HOT</span>
                                    <img src="WWF-TITLEE.jpg"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">Roman Reigns Tribal Chief Poster Autograph 2025</h3>
                                    <div className="product-price">$699.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <img src="US-TITLEE.jpg"/>
                                </div>
                                <div className="product-info">
                                    <h3 className="product-title">WWE US Spinner Championship</h3>
                                    <div className="product-price">$1899.00</div>
                                </div>
                            </div>

                            <div className="product-card">
                                <div className="product-image">
                                    <a href=""><img src="morebeltsbutton.png"/></a>
                                </div>
                                </div>
                            </div>
                        </div>
                </section>

                {/* Brand Highlights */}
                <section className="brand-highlights">
                    <div className="section-container">
                        <h2 className="section-title">Brand Highlights</h2>
                        <p className="brand-subtitle">
                            Join our WWE 2K community group and explore some of our top brand highlights
                        </p>
                        <div className="highlights-grid">
                            <div className="highlight-card">
                                <div className="highlight-icon">
                                    <a href=""><img src="2k-logo.jpg"/></a>
                                </div>
                            </div>
                            <div className="highlight-card">
                                <div className="highlight-icon">
                                    <a href=""><img src="YOUTUBEE.png"/></a>
                                </div>
                            </div>
                            <div className="highlight-card">
                                <div className="highlight-icon">
                                    <a href=""><img src="W.png"/></a>
                                </div>
                            </div>
                            <div className="highlight-card">
                                <div className="highlight-icon">
                                    <a href="Media-kit.pdf" download >< img src="Mediakit.png"/></a>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                {/* Footer */}
                <footer className="footer">
                    <div className="footer-content">
                        <nav className="footer-nav">
                            <button onClick={() => scrollToSection(homeRef)} className="nav-link1">Home</button>
                            <button onClick={() => scrollToSection(shopRef)} className="nav-link1">Shop</button>
                            <a href=""><button className="nav-link1">2K Community</button></a>
                            <button onClick={handleMediaKitDownload} className="nav-link1">Media kit</button>
                            <button onClick={() => console.log('Contact Us clicked')} className="nav-link1">Contact Us</button>
                        </nav>
                        
                        <div className="social-icons">
                            <a href="" className="social-icon"><img src="instagram1.png"/></a>
                            <a href="" className="social-icon"><img src="facebook1.png"/></a>
                            <a href="" className="social-icon"><img src="youtube1.png"/></a>
                        </div>
                        
                        <p className="footer-text">© 2025 BURNITDOWNYT. All rights reserved.</p>
                    </div>
                </footer>
            </div>
        </>
    );
};

export default BIDapp;
