import React, { useEffect, useState } from 'react';

const BurnItDownWebsite = () => {
    const [activeSection, setActiveSection] = useState('home');
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    // Mock data for championship belts - Using your original images
    const championshipBelts = [
        { id: 1, name: "Championship Belt 1", price: "$299.99", image: "/2k25-1.jpg", isHot: true },
        { id: 2, name: "Championship Belt 2", price: "$349.99", image: "/2k25-2.jpg", isHot: true },
        { id: 3, name: "Championship Belt 3", price: "$199.99", image: "/2k25-3.jpg", isHot: false },
        { id: 4, name: "Championship Belt 4", price: "$279.99", image: "/2k25-4.jpg", isHot: true },
        { id: 5, name: "Championship Belt 5", price: "$399.99", image: "/2k25-5.jpg", isHot: false },
        { id: 6, name: "Championship Belt 6", price: "$329.99", image: "/2k25-6.jpg", isHot: true },
        { id: 7, name: "WrestleMania", price: "$249.99", image: "/WW.png", isHot: false },
        { id: 8, name: "Money in the Bank", price: "$369.99", image: "/M.png", isHot: true },
    ];

    // Mock data for latest news (Instagram posts) - Using your original images
    const latestNews = [
        { id: 1, title: "Championship Highlights", isNew: true, image: "/Champ.png", date: "2 hours ago" },
        { id: 2, title: "Behind the Scenes Coverage", isNew: true, image: "/WW.png", date: "4 hours ago" },
        { id: 3, title: "Money in the Bank", isNew: true, image: "/M.png", date: "6 hours ago" },
        { id: 4, name: "Championship Belt 1", isNew: true, image: "/2k25-1.jpg", date: "8 hours ago" },
    ];

    // Mock data for YouTube videos - Using your original images
    const youtubeVideos = [
        { id: 1, title: "165 New Stars Roster Reveal", thumbnail: "/2k25-1.jpg", duration: "4:45", views: "125K", url: "https://www.youtube.com/watch?v=EdYsk6HFPgY" },
        { id: 2, title: "Current Male Roster Reveal", thumbnail: "/2k25-2.jpg", duration: "9:22", views: "89K", url: "https://www.youtube.com/watch?v=UNVNxNmYXn8" },
        { id: 3, title: "Current Female Roster Reveal", thumbnail: "/2k25-3.jpg", duration: "5:38", views: "156K", url: "https://www.youtube.com/watch?v=HaNmPjuoCpQ&t=1s" },
        { id: 4, title: "30 New Stars and 6 New Gimmicks", thumbnail: "/2k25-4.jpg", duration: "2:33", views: "203K", url: "https://www.youtube.com/watch?v=CBXgVaBFyeY" },
    ];

    // Mock data for brand highlights - Using your original social media images
    const brandHighlights = [
        { id: 1, image: "/facebook.png", title: "Facebook Community" },
        { id: 2, image: "/youtube.png", title: "YouTube Channel" },
        { id: 3, image: "/instagram.png", title: "Instagram Stories" },
        { id: 4, image: "/RBID.jpg", title: "Brand Highlights" },
    ];

    const scrollToSection = (sectionId) => {
        const element = document.getElementById(sectionId);
        if (element) {
            const headerHeight = 80;
            const elementPosition = element.offsetTop - headerHeight;
            window.scrollTo({
                top: elementPosition,
                behavior: 'smooth'
            });
        }
        setActiveSection(sectionId);
        setIsMobileMenuOpen(false);
    };

    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = 'Media-kit.pdf';
        link.download = 'mediakit.pdf';
        link.click();
        setIsMobileMenuOpen(false);
    };

    // Close mobile menu when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (isMobileMenuOpen && !event.target.closest('.mobile-menu-container')) {
                setIsMobileMenuOpen(false);
            }
        };

        document.addEventListener('click', handleClickOutside);
        return () => document.removeEventListener('click', handleClickOutside);
    }, [isMobileMenuOpen]);

    return (
        <div className="min-h-screen bg-black text-white overflow-x-hidden">
            <style>{`
                /* Enhanced Mobile-First Responsive Styles */
                :root {
                    --main-red: #c41e3a;
                    --dark-red: #8b1538;
                    --transition: all 0.3s ease;
                }

                * {
                    box-sizing: border-box;
                }

                html {
                    scroll-behavior: smooth;
                }

                .hero-bg {
                    background: linear-gradient(135deg, var(--main-red) 0%, var(--dark-red) 100%);
                }

                .card-hover {
                    transition: var(--transition);
                    cursor: pointer;
                }

                .card-hover:hover {
                    transform: translateY(-4px);
                    box-shadow: 0 10px 30px rgba(196, 30, 58, 0.2);
                }

                .belt-card {
                    background: linear-gradient(145deg, #1a1a1a, #2a2a2a);
                    border: 1px solid #333;
                    border-radius: 12px;
                }

                .hot-badge, .new-badge {
                    background: var(--main-red);
                    color: white;
                    font-size: 10px;
                    font-weight: bold;
                    padding: 4px 8px;
                    border-radius: 4px;
                    position: absolute;
                    top: 8px;
                    left: 8px;
                    z-index: 10;
                    text-transform: uppercase;
                }

                .star-rating {
                    color: #ffd700;
                    font-size: 14px;
                }

                /* Mobile-optimized button styles */
                .mobile-btn {
                    min-height: 44px;
                    padding: 12px 20px;
                    font-size: 16px;
                    border-radius: 8px;
                    border: none;
                    cursor: pointer;
                    transition: var(--transition);
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    text-decoration: none;
                }

                .mobile-btn:active {
                    transform: scale(0.98);
                }

                /* Enhanced touch targets */
                .touch-target {
                    min-height: 44px;
                    min-width: 44px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                /* Video card responsive improvements */
                .video-card {
                    background: #2a2a2a;
                    border-radius: 12px;
                    overflow: hidden;
                    transition: var(--transition);
                    border: 1px solid #333;
                }

                .video-card:hover {
                    transform: translateY(-2px);
                    border-color: var(--main-red);
                }

                .video-duration {
                    position: absolute;
                    bottom: 8px;
                    right: 8px;
                    background: rgba(0, 0, 0, 0.8);
                    color: white;
                    padding: 2px 6px;
                    border-radius: 4px;
                    font-size: 12px;
                    font-weight: 500;
                }

                /* Responsive grid improvements */
                .responsive-grid {
                    display: grid;
                    gap: 1rem;
                    grid-template-columns: 1fr;
                }

                @media (min-width: 480px) {
                    .responsive-grid {
                        grid-template-columns: repeat(2, 1fr);
                        gap: 1.5rem;
                    }
                }

                @media (min-width: 768px) {
                    .responsive-grid {
                        grid-template-columns: repeat(3, 1fr);
                        gap: 2rem;
                    }
                }

                @media (min-width: 1024px) {
                    .responsive-grid {
                        grid-template-columns: repeat(4, 1fr);
                    }
                }

                /* Mobile navigation improvements */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    background: rgba(0, 0, 0, 0.95);
                    backdrop-filter: blur(10px);
                    z-index: 50;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    opacity: 0;
                    visibility: hidden;
                    transition: var(--transition);
                }

                .mobile-menu.open {
                    opacity: 1;
                    visibility: visible;
                }

                .mobile-menu-content {
                    background: #1a1a1a;
                    border-radius: 16px;
                    padding: 2rem;
                    max-width: 90vw;
                    width: 100%;
                    max-width: 400px;
                    border: 1px solid #333;
                }

                /* Improved section spacing */
                .section-mobile {
                    padding: 3rem 1rem;
                }

                .section-tablet {
                    padding: 4rem 2rem;
                }

                .section-desktop {
                    padding: 5rem 2rem;
                }

                @media (min-width: 768px) {
                    .section-mobile {
                        padding: 4rem 2rem;
                    }
                }

                @media (min-width: 1024px) {
                    .section-mobile {
                        padding: 5rem 2rem;
                    }
                }

                /* Enhanced typography scaling */
                .title-mobile {
                    font-size: 2rem;
                    line-height: 1.2;
                }

                .title-tablet {
                    font-size: 2.5rem;
                }

                .title-desktop {
                    font-size: 3rem;
                }

                @media (min-width: 768px) {
                    .title-mobile {
                        font-size: 2.5rem;
                    }
                }

                @media (min-width: 1024px) {
                    .title-mobile {
                        font-size: 3rem;
                    }
                }

                /* Improved text alignment and content layout */
                .text-center {
                    text-align: center;
                }

                .text-left {
                    text-align: left;
                }

                .line-clamp-2 {
                    display: -webkit-box;
                    -webkit-line-clamp: 2;
                    -webkit-box-orient: vertical;
                    overflow: hidden;
                    line-height: 1.4;
                    height: calc(1.4em * 2);
                }

                .content-center {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                /* Enhanced card content alignment */
                .card-content {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    text-align: center;
                    padding: 1rem;
                }

                /* Better image handling */
                .object-contain {
                    object-fit: contain;
                }

                .object-cover {
                    object-fit: cover;
                }

                /* Loading states */
                .skeleton {
                    background: linear-gradient(90deg, #2a2a2a 25%, #3a3a3a 50%, #2a2a2a 75%);
                    background-size: 200% 100%;
                    animation: loading 1.5s infinite;
                }

                @keyframes loading {
                    0% { background-position: 200% 0; }
                    100% { background-position: -200% 0; }
                }

                /* Improved scroll behavior */
                @media (prefers-reduced-motion: reduce) {
                    html {
                        scroll-behavior: auto;
                    }

                    .card-hover {
                        transition: none;
                    }
                }
            `}</style>

            {/* Enhanced Header */}
            <header className="fixed top-0 w-full bg-black/95 backdrop-blur-md z-50 border-b border-gray-800/50">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex items-center justify-between h-16 lg:h-20">
                        {/* Logo - Enhanced for mobile */}
                        <div className="flex items-center space-x-2 lg:space-x-3">
                            <img src="RBID.jpg" alt="Burn It Down Logo" className="w-8 h-8 lg:w-10 lg:h-10 rounded-lg" />
                            <h1 className="text-lg lg:text-xl font-bold text-white truncate" style={{fontFamily: 'League Gothic, Impact, sans-serif'}}>BURNITDOWNYT</h1>
                        </div>

                        {/* Desktop Navigation */}
                        <nav className="hidden lg:flex items-center space-x-6 xl:space-x-8">
                            <button onClick={() => scrollToSection('home')} className="hover:text-red-500 transition-colors font-medium">HOME</button>
                            <button onClick={() => scrollToSection('shop')} className="hover:text-red-500 transition-colors font-medium">SHOP</button>
                            <button onClick={() => scrollToSection('news')} className="hover:text-red-500 transition-colors font-medium">LATEST NEWS</button>
                            <button onClick={handleMediaKitDownload} className="hover:text-red-500 transition-colors font-medium">MEDIAKIT</button>
                            <a href="mailto:burnitdownyt@email.com" className="hover:text-red-500 transition-colors font-medium">CONTACT US</a>
                        </nav>

                        {/* Desktop Search Bar */}
                        <div className="hidden lg:flex items-center">
                            <input
                                type="text"
                                placeholder="Search Videos..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="bg-gray-800/80 text-white px-4 py-2 rounded-l-lg border border-gray-600 focus:outline-none focus:border-red-500 w-48 xl:w-56"
                            />
                            <button className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded-r-lg transition-colors">
                                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                </svg>
                            </button>
                        </div>

                        {/* Mobile Menu Button */}
                        <button 
                            className="lg:hidden touch-target"
                            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        >
                            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                            </svg>
                        </button>
                    </div>
                </div>
            </header>

            {/* Enhanced Mobile Menu */}
            <div className={`mobile-menu mobile-menu-container ${isMobileMenuOpen ? 'open' : ''}`}>
                <div className="mobile-menu-content">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="text-xl font-bold">Menu</h3>
                        <button 
                            onClick={() => setIsMobileMenuOpen(false)}
                            className="touch-target text-gray-400 hover:text-white"
                        >
                            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                            </svg>
                        </button>
                    </div>

                    <nav className="space-y-4 mb-6">
                        <button onClick={() => scrollToSection('home')} className="mobile-btn bg-gray-800 hover:bg-gray-700 text-white w-full">HOME</button>
                        <button onClick={() => scrollToSection('shop')} className="mobile-btn bg-gray-800 hover:bg-gray-700 text-white w-full">SHOP</button>
                        <button onClick={() => scrollToSection('news')} className="mobile-btn bg-gray-800 hover:bg-gray-700 text-white w-full">LATEST NEWS</button>
                        <button onClick={handleMediaKitDownload} className="mobile-btn bg-gray-800 hover:bg-gray-700 text-white w-full">MEDIAKIT</button>
                        <a href="mailto:burnitdownyt@email.com" className="mobile-btn bg-gray-800 hover:bg-gray-700 text-white w-full">CONTACT US</a>
                    </nav>

                    <div className="border-t border-gray-700 pt-4">
                        <div className="flex">
                            <input
                                type="text"
                                placeholder="Search videos..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="bg-gray-800 text-white px-4 py-3 rounded-l-lg border border-gray-600 focus:outline-none focus:border-red-500 flex-grow"
                            />
                            <button className="bg-red-600 hover:bg-red-700 px-4 py-3 rounded-r-lg transition-colors">
                                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                </svg>
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Enhanced Home Section */}
            <section id="home" className="hero-bg section-mobile pt-20 lg:pt-24">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex flex-col lg:flex-row items-center min-h-[calc(100vh-5rem)]">
                        <div className="w-full lg:w-1/2 text-center lg:text-left mb-8 lg:mb-0">
                            <div className="flex justify-center lg:justify-start mb-6 lg:mb-8">
                                <img src="RBID.jpg" alt="Burn It Down Logo" className="w-24 h-24 sm:w-32 sm:h-32 rounded-full shadow-2xl object-cover" />
                            </div>
                            <h1 className="title-mobile font-bold mb-4 lg:mb-6">
                                Listen to<br />
                                the amazing<br />
                                music sound.
                            </h1>
                            <p className="text-lg lg:text-xl mb-6 lg:mb-8 opacity-90 max-w-md mx-auto lg:mx-0">
                                Experience music like never before
                            </p>
                            <button 
                                onClick={() => scrollToSection('shop')}
                                className="mobile-btn bg-black hover:bg-gray-900 text-white font-semibold"
                            >
                                Shopping Now
                            </button>
                        </div>
                        <div className="w-full lg:w-1/2 mt-8 lg:mt-0">
                            <div className="w-full h-64 sm:h-80 lg:h-96 bg-black/20 rounded-2xl overflow-hidden">
                                <img src="/WW.png" alt="WrestleMania Hero" className="w-full h-full object-cover" />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Enhanced Features Bar */}
