import { useState, useRef } from 'react';

const Product = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDiscountVisible, setIsDiscountVisible] = useState(true);
    // Refs for scrolling to sections
    const homeRef = useRef(null);
    const shopRef = useRef(null);
    const latestNewsRef = useRef(null);

    const scrollToSection = (ref) => {
        ref.current?.scrollIntoView({ behavior: 'smooth' });
        setIsMobileMenuOpen(false);
    };
    const handleMediaKitDownload = () => {
        const link = document.createElement('a');
        link.href = 'Media-kit.pdf';
        link.download = 'MediaKit.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setIsMobileMenuOpen(false);
    };

    // Safe icon components using Unicode/HTML entities
    const MenuIcon = () => <span style={{fontSize: '20px'}}>☰</span>;
    const CloseIcon = () => <span style={{fontSize: '20px'}}>✕</span>;
    const UserIcon = () => <span style={{fontSize: '18px'}}><img src="Vector.png"/></span>;
    const ShoppingBagIcon = () => <span style={{fontSize: '18px'}}><img src="shoppingbag.png"/></span>;

    // State for quantity, wishlist, etc.
    const [quantity, setQuantity] = useState(1);
    const [isWishlisted, setIsWishlisted] = useState(false);
    const [showAdditionalInfo, setShowAdditionalInfo] = useState(true);

    // --- Product Data ---
    // This data can come from props, context, or an API call.
    const product = {
        id: 'wwe-undisputed-cena',
        name: "Tray Table", // Updated name to match the example image
        price: 199.00,
        originalPrice: 400.00,
        images: [
            "WWEBELTCENA.png",
            "UNDISPUTEDTITLE.png",
            // TODO: Replace this with the actual path to your contact image
            "YOUR_CONTACT_IMAGE.jpg" 
        ],
        description: "Buy one or buy a few and make every space where you sit more convenient. Light and easy to move around with removable tray top, handy for serving snacks."
    };

    // --- Related Products Data ---
    const relatedProducts = [
        {
            id: 1,
            name: "Loveseat Sofa", // Updated name to match the example image
            price: 199.00,
            originalPrice: 400.00,
            image: "WIGNEDEAGLE_.png",
            badge: "NEW",
            discount: "-10%"
        },
        {
            id: 2,
            name: "Table Lamp", // Updated name to match the example image
            price: 24.99,
            image: "WORLDTITLEE.png"
        },
        {
            id: 3,
            name: "Beige Table Lamp", // Updated name to match the example image
            price: 24.99,
            image: "WORLDTITLE.png"
        },
        {
            id: 4,
            name: "Bamboo basket", // Updated name to match the example image
            price: 24.99,
            image: "NXTTITLE.png"
        },
    ];

    // --- Event Handlers ---
    const handleQuantityChange = (change) => {
        setQuantity(prev => Math.max(1, prev + change));
    };

    // This function simulates navigation. Replace with your actual routing logic.
    const navigateToProduct = (productId) => {
        // In a real app, you'd use something like:
        // history.push(`/product/${productId}`);
        alert(`Navigating to product with ID: ${productId}`);
    };
    return (
        <>
            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                body {
                    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    background-color: white;
                    color: black;
                    line-height: 1.6;
                }

                .container {
                    min-height: 100vh;
                    background-color: white;
                }

                /* Discount Banner */
                .discount-banner {
                    background-color: #D3D3D3;
                    color: black;
                    text-align: center;
                    padding: 0.5rem;
                    font-size: 0.85rem;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 0.5rem;
                    position: relative;
                }

                .discount-banner .discount-icon {
                    display:flex;
                    align-items:center;

            }
                .discount-close {
                    position: absolute;
                    right: 1rem;
                    background: none;
                    border: none;
                    color: white;
                    cursor: pointer;
                    font-size: 18px;
                    padding: 0.2rem;
                    display: flex;
                    align-items: center;
                    justify-content: center;
}

.discount-close:hover {
    color: #ccc;
}                 

                .discount-banner .shop-show {
                    color: $fff;
                    text-decoration: none;
                    font-weight: 600;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;


                }
                .discount-banner .shop-now img {
                    width: 1rem;
                    height: auto;
                }

                /* Header Styles */
                .header {
                    background-color: white;
                    border-bottom: 1px solid #e0e0e0;
                    padding: 0.8rem 0;
                    position: sticky;
                    top: 0;
                    z-index: 1000;
                }

                .header-content {
                    max-width: 1200px;
                    margin: 0 auto;
                    display: flex;

                    justify-content: space-between;
                    align-items: center;
                    padding: 0 1rem;
                    background-color: white;
                    position: relative;
                }


                .logo {
                    font-size: 1.4rem;
                    font-weight: bold;
                    color: #000;
                    text-decoration: none;
                    background-color: white;
                    flex: 1;
                    margin-left: 0.5rem;

                }

                .desktop-nav {
                    display: none;
                    gap: 2rem;
                }

                .nav-link {
                    background: none;
                    border: none;
                    color: Black;
                    background-color: white;
                    text-decoration: none;
                    font-size: 0.9rem;
                    cursor: pointer;
                    padding: 0.5rem 0;
                    font-weight: 500;
                    transition: color 0.3s;
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    white-space: nowrap;
                }
                .nav-link img {
                   display: inline-block;
                   width: 1rem;
                   height: auto;
                   margin: 0;
                }
                .nav-arrow {
                   width: 1rem;
                   height:auto;
                   margin-right: 0.25rem;!important
                   display: inline-block;

                }


                .nav-link:hover {
                    color: #000;
                    background-color: white;
                }

                .nav-link1.no-underline {
                   text-decoration: none;

                }

                .header-right {
                    display: flex;
                    align-items: center;
                    gap: 0.2rem;
                    background-color: white;
                    flex: 1; /* Take up available space */
                    justify-content: flex-end;
                }

                .header-icon {
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                    border-radius: 4px;
                    transition: background-color 0.3s;
                }

                .header-icon:hover {
                    background-color: #f5f5f5;
                }

                .mobile-menu-btn {
                    background: none;
                    border: none;
                    color: #333;
                    cursor: pointer;
                    padding: 0.5rem;
                }

                /* Mobile Menu */
                .mobile-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 10;
                    background-color: white;
                    z-index: 2000;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                }

                .mobile-menu-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 2rem;
                }

                .mobile-menu-header .logo {
                    color: white;
                }

                .mobile-nav {
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem;
                    background-color: white;
                    color: white;
                }

                .mobile-nav .nav-link {
